	ds_list.SetColumn(ds_list.RowCount()-1,'ZPSTAT',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZBCDAT',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZBEDAT',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZTRDAT',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZRCDAT',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZFCMNT',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZFMMNT',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZFCDA',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZPCDA',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZFDAT',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZPDAT',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZSESDAT',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZCOPRC',' ');
	ds_list.SetColumn(ds_list.RowCount()-1,'ZCOMNT',' ');
	if (G_VEND_WGBN == 'A' || G_VEND_WGBN == 'B' || G_VEND_WGBN == 'C' || G_VEND_WGBN == 'D' || G_VEND_WGBN == 'E') {
		ds_list.SetColumn(ds_list.RowCount()-1,'ZAGNT',G_VEND_CODE);
		alert(G_VEND_NAME);
//		ds_list.SetColumn(ds_list.RowCount()-1,'ZAGNT_NM',G_VEND_NAME);
	} else {
		ds_list.SetColumn(ds_list.RowCount()-1,'ZAGNT',' ');
//		ds_list.SetColumn(ds_list.RowCount()-1,'ZAGNT_NM',' ');
	}
	ds_list.Fireevent = true;
	if (parseInt(substr(gfn_getCurrDate(),6,2)) > 10) {
		ds_list.SetColumn(ds_list.RowCount()-1,'SORLT','20');
	} else {
		ds_list.SetColumn(ds_list.RowCount()-1,'SORLT','10');
	}
}

/********************************************************************************
* 기      능   : 그리드 row 삭제
********************************************************************************/
function fn_minus() {
	if (ds_delete.GetColumn(0,'ROW') > F_ROWCOUNT) {
		ds_list.DeleteRow(ds_delete.GetColumn(0,'ROW'));
	}
}

