	eval( fncName + "()");
}

/********************************************************************************
* 기      능   : 그리드 셀클릭
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == 'GTYPE') {
		obj.Fireevent = false;
		ds_list.SetColumn(nRow,'AUART',ds_gtypes.GetColumn(ds_gtype.SearchRow("CODE='" + varNewValue + "'"), 'Auart'));
		ds_list.SetColumn(nRow,'MATNR',ds_gtypes.GetColumn(ds_gtype.SearchRow("CODE='" + varNewValue + "'"), 'Matnr'));
		ds_list.SetColumn(nRow,'SPART',ds_gtypes.GetColumn(ds_gtype.SearchRow("CODE='" + varNewValue + "'"), 'Spart'));
		obj.Fireevent = true;
	}

	// 값이 변경되었을 경우 변경구분 컬럼 자동 SET 
	if (varNewValue != varOldValue) {
		obj.Fireevent = false;
		ds_list.SetColumn(nRow,'CHK','1');
		obj.Fireevent = true;

		// FLAG 변경 : U
		gfn_changeFlag(obj, strColumnID, nRow, "FLAG");
	}

	if (strColumnID != 'FLAG') {
		F_MERGE = true;
	}

	if (strColumnID != 'CHK') return;
}

function grd_list_OnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam,nRow,nCell,nPivotIndex)
{
	fn_save_row(nRow);
	F_MERGE = true;
	ds_list.SetColumn(nRow,'CHK','1');

	if (nCell == gfn_getColIdx(grd_list,"ZPYM")) {
		F_ZPYMROW = nRow;
		F_ZPYMCELL = nCell;
	}
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	ds_delete.SetColumn(0,'ROW',nRow);
	if (ds_list.GetColumn(nRow,'SORLT') == '10' || ds_list.GetColumn(nRow,'SORLT') == '20' || ds_list.GetColumn(nRow,'SORLT') == '30' || ds_list.GetColumn(nRow,'SORLT') == '40') {
		if (ds_list.GetColumn(nRow,'FLAG') == 'I') {
			btn_sorlt.Visible = false;
			F_SORLTROW = -1;
		} else {
			btn_sorlt.Visible = true;
			F_SORLTROW = nRow;
		}
	} else {
		btn_sorlt.Visible = false;
		F_SORLTROW = -1;
	}
}

function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if (grd_list.GetCellProp("body",F_ZPYMCELL,"ColId") == 'ZPYM') {
		if (ParseInt(substr(gfn_getCurrDate(),0,6)) > ParseInt(ds_list.GetColumn(F_ZPYMROW,'ZPYM'))) {
			if (nCell != gfn_getColIdx(grd_list,"ZPYM")) {
				alert('계획년월은 현재월 이후로 입력하세요.');
				gfn_SetFocusGrid(grd_list,F_ZPYMROW,F_ZPYMCELL);
			}
		} else {
			if (nCell != gfn_getColIdx(grd_list,"ZPYM")) {
				if (ds_delete.GetColumn(0,'ROW') > F_ROWCOUNT) {
					if (ParseInt(substr(gfn_getCurrDate(),0,6)) == ParseInt(ds_list.GetColumn(F_ZPYMROW,'ZPYM'))) {
						if (parseInt(substr(gfn_getCurrDate(),6,2)) > 10) {
							ds_list.SetColumn(ds_list.RowCount()-1,'SORLT','20');
						} else {
							ds_list.SetColumn(ds_list.RowCount()-1,'SORLT','10');
						}
					} else {
						ds_list.SetColumn(ds_list.RowCount()-1,'SORLT','10');
					}
				}
			}
		}
	}
}

function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if (nCell == gfn_getColIdx(grd_list,"KUNNR"))
	{
		var arg = "fa_knur_cd= " 	+ quote(grd_list.GetCellText('Body',nRow,17));

		var arr = gfn_openPopUpUrlHdel("Com::Com0040.xml", true, arg);

		if (arr != null && arr != 0) {
			F_MERGE = true;
			ds_list.SetColumn(nRow,'CHK','1');
			ds_list.SetColumn(nRow,'KUNNR',arr[0]);
			ds_list.SetColumn(nRow,'KUNNR_NM',arr[1]);
			ds_list.SetColumn(nRow,'LAND1',arr[4]);
			ds_list.SetColumn(nRow,'LAND1_NM',arr[5]);
			ds_list.SetColumn(nRow,'ZAREA_NM',arr[7]);
		} 
	}
}
