//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	fn_init();

	gfn_translation(obj, G_LANG);
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj)
{
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init()
{
	gfn_initForm(true);
	div_sales.fn_reSize	(85, "1", "~", 15, "1", 85, ''	);  // SO 사이즈 조정	(FROM, TO 모두 처리)
	div_buyr.fn_reSize	(84, "1", "~", 15, "1", 84, '1'	);  // 거래선 사이즈 조정 (FROM만 처리)  

	/* 수주기간 기본값 SETTING */ 
	cal_frymd.value = substr(gfn_getCurrDate(),0,6) + "01";	// 시작일자 = 현재년월 + 01
	cal_toymd.value = gfn_getCurrDate();	 					// 종료년월 = 현재년월일

	fn_init_auth();  
}

/********************************************************************************
* 기      능   : 사용자 권한별 설정
********************************************************************************/ 
function fn_init_auth()
{ 
	fn_nsfRequest("combo","comboAuart","ds_auart_cd=ds_auart_cd","ds_auart_cd=ds_auart_cd","fn_afterQuery_auart");
	gfn_InsertRowCombo(ds_spart_cd, 0, "Y", "N"); // 종료팀코드목록에 전체 행 추가
	cb_spart.index = 0;
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 컨트롤러 전송
********************************************************************************/
function fn_nsfRequest(url1,url2,ds1,ds2,callback) {
	nsfRequest("query", url1+"/"+url2, ds1, ds2+" ds_error=ds_error", "", callback);
}

/********************************************************************************
* 기      능   : 버튼 이벤트
********************************************************************************/
function fn_OnClick_btn(obj)
{ 
	var fncName = "fn_" + substr(obj.id, 4); 
	eval( fncName + "()");
}

/********************************************************************************
* 기      능   : 기준 선택
********************************************************************************/
function rd_gubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode = "D")
	{
		grd_list.SetColProp(13,"width",80);
		grd_list.SetColProp(14,"width",80);
		grd_list.SetColProp(15,"width",80);
	}
	else
	{
		grd_list.SetColProp(13,"width",0);
		grd_list.SetColProp(14,"width",0);
		grd_list.SetColProp(15,"width",0);
	}
}

/********************************************************************************
* 기      능   : 조회
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {
	var calFrymd;
	var calToymd;
	var rdCode;
	var auart;
	var spart;

	calFrymd = cal_frymd.Text;
	calFrymd = calFrymd.Replace('.', '-');

	calToymd = cal_toymd.Text;
	calToymd = calToymd.Replace('.', '-');

	var arr = ['청구일자', '12'];
	if(gfn_getDayCnt(ds_days, cal_frymd.Value, cal_toymd.Value) > 365) {
		gfn_showAlert("CW10087", arr); //청구일자는 12개월 내에서 조회할 수 있습니다. \n확인하시기 바랍니다.
		cal_frymd.SetFocus();
		return false;
	}

	if (rd_gubun.Index == 0) {
		rdCode = "D";
	}
	else if (rd_gubun.Index == 1) {
		rdCode = "M";
	}

	if (cb_auart.Value == null) {
		auart = "";
	} else {
		auart = cb_auart.Value;
	}

	if (cb_spart.Value == null) {
		spart = "";
	} else {
		spart = cb_spart.Value;
	}

	ds_cond.DeleteAll();
	ds_cond.AddRow();
	ds_cond.SetColumn(0,"I_FR_YMD",calFrymd);
	ds_cond.SetColumn(0,"I_TO_YMD",calToymd);
	ds_cond.SetColumn(0,"I_FR_SO",div_sales.ed_fromEdit.Text);
	ds_cond.SetColumn(0,"I_TO_SO",div_sales.ed_toEdit.Text);
	ds_cond.SetColumn(0,"I_FR_VB",div_vbvg.cb_frvb.Value);
	ds_cond.SetColumn(0,"I_TO_VB",div_vbvg.cb_tovb.Value);
	ds_cond.SetColumn(0,"I_FR_VG",div_vbvg.cb_frvg.Value);
	ds_cond.SetColumn(0,"I_TO_VG",div_vbvg.cb_tovg.Value);
	ds_cond.SetColumn(0,"I_SMAN",div_sman.ed_sman_cd.Text);
	ds_cond.SetColumn(0,"I_BUYR",div_buyr.ed_buyr_cd.Text);
	ds_cond.SetColumn(0,"I_NAME",ed_name.Text);
	ds_cond.SetColumn(0,"I_AUART",auart);
	ds_cond.SetColumn(0,"I_SPART",spart);
	ds_cond.SetColumn(0,"I_GUBUN",rdCode);

	// SubForm 데이타셋을 Copy해옴  : 빈행제외
	gfn_copyDs(div_sales.Ds_rtn, ds_cond_so, GC_SO_FILTER_COND); 	// so 범위조건
	gfn_copyDs(div_buyr.Ds_rtn, ds_cond_buyr, GC_BUYR_FILTER_COND); 	// 거래선 범위조건

	gfn_clearMsgBar();
	var user_grp = gfn_getUserGroup(); 

	// 조회조건 항목오류검증
	if (fn_valid_chk("query") == false)  return;

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}
	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);

	// 출력데이타셋 초기화
	ds_list.ClearData();  

	// 서비스 호출
	fn_nsfRequest("sch0020"
				, "search"
				, "ds_cond=ds_cond ds_cond_so=ds_cond_so ds_cond_buyr=ds_cond_buyr ds_result=ds_result"
				, "ds_list=ds_list ds_error=ds_error"
				, "fn_afterQuery");

    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	// Excel Export 
	if (ds_list.RowCount() > 0)
		gfn_exportExcel(grd_list, "월별청구대장");
	else
		gfn_showAlert("CW00054");	
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj, nCell);
	}
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	if(ds_error.rowcount() > 0) {   

		gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG"));   

	} else {

		// 조회된 자료가 있을 경우 
		if (ds_list.RowCount() > 0) 
		{ 
			gfn_showMsg("CI00002", ds_list.RowCount()+"");  // CI00002 : "조회를 완료했습니다.조회건수 [${}건]"

			grd_list.SetFocus();

		} 
		else 
		{	 
			gfn_showMsg("CW00010"); 					// CW00010 : "데이터가 없습니다."
		}

	}
}

/********************************************************************************
* 기      능   : 오더유형 조회 Callback  
********************************************************************************/
function fn_afterQuery_auart(errCode, errMsg) 
{  
	gfn_InsertRowCombo(ds_auart_cd, 0, "Y", "N"); // 종료팀코드목록에 전체 행 추가
	cb_auart.index = 0;
}

/********************************************************************************
* 기      능   : valid 체크 
********************************************************************************/
function fn_valid_chk(pProcGb)
{ 

	switch(pProcGb)
	{
		case "query" :   // 조회
			if (length(ed_frso.Text) == 0)
			{
				if (length(div_vbvg.cb_frvb.Value) == 0)
				{
					// CW00004=[${}] 항목이 입력될 경우 [${}] 항목은 필수입력입니다.\n확인하여 주십시오.
					gfn_showAlert('CW00004',['수주번호','부서코드']);
					gfn_SetFocus(div_vbvg.cb_frvb,"Y"); 
					return false;
				}
			}
			else
			{
				if (length(ed_toso.Text) == 0)
				{
					// CW10026=${} 입력시에는 범위입력 하셔야 합니다.
					gfn_showAlert('CW10026','수주번호');
					gfn_SetFocus(ed_toso,"Y"); 
					return false;
				}
			}
			break; 

		case "query_buyr" :   // 거래선조회
			break;	

		case "excel" :   // 엑셀
			break;

		default: 
			break; 
	}   

	return true;
}
