//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var F_INIT = false;
var F_AMT = false;
var F_CHAR = false;
var F_COLUMN = 0;
var F_ROW = 0;
var F_TOP    = 192;
var F_HEIGTH = 380;

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	fn_init();
}

/********************************************************************************
* form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj)
{
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init()
{
	gfn_initForm(true);

	fn_init_combo();
	fn_init_auth();

	// 선택에 따른 그리드 변경
	rd_KIJUN_OnChanged(rd_KIJUN,'A','호기',0,0);

	fn_CDATE();
	F_INIT = true;

	this.OnSize = "Sch0060_OnSize";
	Sch0060_OnSize(this);
}

