/********************************************************************************
* 기      능   : 사용자 권한별 설정
********************************************************************************/ 
function fn_init_auth() {
	ds_cond.DeleteAll();
	ds_cond.AddRow();

	// 기준년월, 수주월, 출하월, 착공월
	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-10,1);
	gfn_InsertRowCombo(ds_yearf_cd, 0, "Y", "N"); // 전체 행 추가
	cb_CDATE_MM.index = parseInt(substr(gfn_getCurrDate(),4,2));
	cb_BSTDK_FRYY.index = 0;
	cb_BSTDK_TOYY.index = 0;
	cb_CLDATE1_FRYY.index = 0;
	cb_CLDATE1_TOYY.index = 0;
	cb_ZZSHIP1_FRYY.index = 0;
	cb_ZZSHIP1_TOYY.index = 0;

	// 영업
	div_vbvg.Static0.visible = false;
	div_vbvg.cb_frvb.left = 0;
	div_vbvg.cb_frvb.width = 80;
	div_vbvg.st_from_to_vb.visible = false;
	div_vbvg.cb_tovb.visible = false;
	div_vbvg.Static1.visible = false;
	div_vbvg.cb_frvg.left = 83;
	div_vbvg.cb_frvg.width =80;
	div_vbvg.st_from_to_vg.visible = false;
	div_vbvg.cb_tovg.visible = false;
	div_vbvg.Static2.visible = false;
	div_vbvg.ed_sman_cd.left = 166;
	div_vbvg.ed_sman_cd.width =80;
	div_vbvg.btn_popSman.left = 246;

	// 수주처
	div_buyr.fr_ed_buyr_cd.left = 0;
	div_buyr.fr_ed_buyr_cd.width = 170;
	div_buyr.btn_fr_popBuyr.left = 170;
	div_buyr.to_ed_buyr_cd.visible = false;
}

