/********************************************************************************
* 기      능   : 버튼 이벤트
********************************************************************************/
function fn_OnClick_btn(obj)
{ 
	var fncName = "fn_" + substr(obj.id, 4); 
	eval( fncName + "()");
}

/********************************************************************************
* 기      능   : 수주협력업체  
********************************************************************************/
function fn_KUNZ1() {

	// 수주협력업체조회 팝업화면 OPEN
	var arg = "fa_code=" 	+ quote(ed_KUNZ1.Text)
			+ " fa_name=" 		+ quote('수주협력업체')
			+ " fa_sortl=" 		+ quote('대리점')
			;

	var arr = gfn_openPopUpUrlHdel("Sch::Sch0061.xml", true, arg );

	if (arr != null && arr != 0)
	{
		ed_KUNZ1.text = arr[0];
		ed_KUNZ1.SetFocus();
	}
}

/********************************************************************************
* 기      능   : 설치협력업체  
********************************************************************************/
function fn_LIFNR() {

	// 설치협력업체조회 팝업화면 OPEN
	var arg = "fa_code=" 	+ quote(ed_LIFNR.Text)
			+ " fa_name=" 		+ quote('설치협력업체')
			+ " fa_sortl=" 		+ quote('설치협력업체')
			;

	var arr = gfn_openPopUpUrlHdel("Sch::Sch0061.xml", true, arg );

	if (arr != null && arr != 0)
	{
		ed_LIFNR.text = arr[0];
		ed_LIFNR.SetFocus();
	}
}

/********************************************************************************
* 기      능   : 조회
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {
	// 조회조건 항목오류검증
	if (fn_valid_chk(rd_KIJUN.Value) == false) return;

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	fn_param_query();
	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	// 서비스 호출
	nsfRequest("query"									// srvId
				, "sch0060/find"						// url
				, "ds_cond=ds_cond ds_list=ds_list"		// inDsList
				, "ds_list=ds_list"						// outDsList
				, ""									// strParam
				, "fn_afterQuery"						// CallBackFunc
				);
}

/********************************************************************************
* 기      능   : 저장  
********************************************************************************/
function fn_save() {
	if (rd_KIJUN.Value == 'A' or rd_KIJUN.Value == 'B') {
		alert('청구, 수금만 저장이 가능합니다.');
		return;
	} else if (rd_KIJUN.Value == 'C') {	// 청구
		ds_list_save.DeleteAll();
		for (var nRow = 0; nRow < ds_list.RowCount(); nRow++) {
			if (ds_list.GetColumn(nRow,'CHK') == '1' and F_AMT == true) {
				if (parseInt(ds_list.GetColumn(nRow,'AMT00')) >= 0) {
					ds_list_save.AddRow();
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VBELN',ds_list.GetColumn(nRow,'VBELN'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'PSPID',ds_list.GetColumn(nRow,'PSPID'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'SPART',ds_list.GetColumn(nRow,'SPART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'AUART',ds_list.GetColumn(nRow,'AUART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKBUR',ds_list.GetColumn(nRow,'VKBUR'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKGRP',ds_list.GetColumn(nRow,'VKGRP'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'WAERK',ds_list.GetColumn(nRow,'WAERK'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'ZRQYM',ds_cond.GetColumn(0, "AMT00")  );
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'NETWR_RQ',ds_list.GetColumn(nRow,'AMT00'));
				}
				if (parseInt(ds_list.GetColumn(nRow,'AMT01')) >= 0) {
					ds_list_save.AddRow();
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VBELN',ds_list.GetColumn(nRow,'VBELN'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'PSPID',ds_list.GetColumn(nRow,'PSPID'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'SPART',ds_list.GetColumn(nRow,'SPART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'AUART',ds_list.GetColumn(nRow,'AUART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKBUR',ds_list.GetColumn(nRow,'VKBUR'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKGRP',ds_list.GetColumn(nRow,'VKGRP'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'WAERK',ds_list.GetColumn(nRow,'WAERK'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'ZRQYM',ds_cond.GetColumn(0, "AMT01")  );
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'NETWR_RQ',ds_list.GetColumn(nRow,'AMT01'));
				}
				if (parseInt(ds_list.GetColumn(nRow,'AMT02')) >= 0) {
					ds_list_save.AddRow();
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VBELN',ds_list.GetColumn(nRow,'VBELN'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'PSPID',ds_list.GetColumn(nRow,'PSPID'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'SPART',ds_list.GetColumn(nRow,'SPART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'AUART',ds_list.GetColumn(nRow,'AUART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKBUR',ds_list.GetColumn(nRow,'VKBUR'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKGRP',ds_list.GetColumn(nRow,'VKGRP'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'WAERK',ds_list.GetColumn(nRow,'WAERK'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'ZRQYM',ds_cond.GetColumn(0, "AMT02")  );
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'NETWR_RQ',ds_list.GetColumn(nRow,'AMT02'));
				}
				if (parseInt(ds_list.GetColumn(nRow,'AMT03')) >= 0) {
					ds_list_save.AddRow();
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VBELN',ds_list.GetColumn(nRow,'VBELN'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'PSPID',ds_list.GetColumn(nRow,'PSPID'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'SPART',ds_list.GetColumn(nRow,'SPART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'AUART',ds_list.GetColumn(nRow,'AUART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKBUR',ds_list.GetColumn(nRow,'VKBUR'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKGRP',ds_list.GetColumn(nRow,'VKGRP'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'WAERK',ds_list.GetColumn(nRow,'WAERK'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'ZRQYM',ds_cond.GetColumn(0, "AMT03")  );
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'NETWR_RQ',ds_list.GetColumn(nRow,'AMT03'));
				}
				if (parseInt(ds_list.GetColumn(nRow,'AMT04')) >= 0) {
					ds_list_save.AddRow();
