				}
				if (parseInt(ds_list.GetColumn(nRow,'AMT10')) >= 0) {
					ds_list_save.AddRow();
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VBELN',ds_list.GetColumn(nRow,'VBELN'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'PSPID',ds_list.GetColumn(nRow,'PSPID'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'SPART',ds_list.GetColumn(nRow,'SPART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'AUART',ds_list.GetColumn(nRow,'AUART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKBUR',ds_list.GetColumn(nRow,'VKBUR'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKGRP',ds_list.GetColumn(nRow,'VKGRP'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'WAERK',ds_list.GetColumn(nRow,'WAERK'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'BYSYM',ds_cond.GetColumn(0, "AMT10")  );
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'COLBI',ds_list.GetColumn(nRow,'AMT10'));
				}
				if (parseInt(ds_list.GetColumn(nRow,'AMT11')) >= 0) {
					ds_list_save.AddRow();
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VBELN',ds_list.GetColumn(nRow,'VBELN'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'PSPID',ds_list.GetColumn(nRow,'PSPID'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'SPART',ds_list.GetColumn(nRow,'SPART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'AUART',ds_list.GetColumn(nRow,'AUART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKBUR',ds_list.GetColumn(nRow,'VKBUR'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKGRP',ds_list.GetColumn(nRow,'VKGRP'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'WAERK',ds_list.GetColumn(nRow,'WAERK'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'BYSYM',ds_cond.GetColumn(0, "AMT11")  );
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'COLBI',ds_list.GetColumn(nRow,'AMT11'));
				}
				if (parseInt(ds_list.GetColumn(nRow,'AMT12')) >= 0) {
					ds_list_save.AddRow();
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VBELN',ds_list.GetColumn(nRow,'VBELN'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'PSPID',ds_list.GetColumn(nRow,'PSPID'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'SPART',ds_list.GetColumn(nRow,'SPART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'AUART',ds_list.GetColumn(nRow,'AUART'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKBUR',ds_list.GetColumn(nRow,'VKBUR'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'VKGRP',ds_list.GetColumn(nRow,'VKGRP'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'WAERK',ds_list.GetColumn(nRow,'WAERK'));
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'BYSYM',ds_cond.GetColumn(0, "AMT12")  );
					ds_list_save.SetColumn(ds_list_save.RowCount()-1,'COLBI',ds_list.GetColumn(nRow,'AMT12'));
				}
			}
		}
	}

	if (ds_list_save.RowCount() > 0) {
		// 서비스 호출
		nsfRequest("query"																// srvId
					, "sch0060/save"													// url
					, "ds_cond=ds_cond ds_list=ds_list ds_list_save=ds_list_save"		// inDsList
					, "ds_list=ds_list"													// outDsList
					, ""																// strParam
					, "fn_afterQuery"													// CallBackFunc
					);
	} else {
		Alert('저장할 값이 업습니다.');
	}
}

