/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--) {
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}

	cb_CDATE_YY.index = eNum;
}

function fn_CDATE() {
	var bef_month = cb_CDATE_YY.Value + cb_CDATE_MM.Value + '01';
	var aft_month = "";

	// 편성년도의 01월부터 18개월 생성
	for (var i=0; i<13; i++)
	{
		// 한달 씩 더한다.
		aft_month = AddMonth(bef_month,i);

		grd_listC.SetCellProp("Head"
							, parseInt(19) + i	
							, "Text"
							, substr(aft_month,0,4)+"."+substr(aft_month,4,2)+""  // YYYY.MM
							);

		grd_listD.SetCellProp("Head"
							, parseInt(24) + i	
							, "Text"
							, substr(aft_month,0,4)+"."+substr(aft_month,4,2)+""  // YYYY.MM
							);
	}

	ds_combo.ClearData();
	ds_combo_gubun.ClearData();
	ds_combo_gubun.AddRow();
	ds_combo_gubun.SetColumn(0, "GUBUN", 'ds_cond_CDATE');
	ds_cond_CDATE.ClearData();
	ds_cond_CDATE.AddRow();
	ds_cond_CDATE.SetColumn(0, "CDATE_YY", cb_CDATE_YY.Value);
	ds_cond_CDATE.SetColumn(0, "CDATE_MM", cb_CDATE_MM.Value);

	// 서비스 호출
	nsfRequest("CDATE"																			// srvId
				, "sch0060/combo"																// url
				, "ds_combo_gubun=ds_combo_gubun ds_cond_CDATE=ds_cond_CDATE ds_combo=ds_combo"	// inDsList
				, "ds_combo=ds_combo"															// outDsList
				, ""																			// strParam
				, "fn_afterQuery_combo"															// CallBackFunc
				);
}

function fn_VISIBLE(value) {
	cb_ZSPEC1.Enable = value;
	cb_ZSPEC4.Enable = value;
	cb_ZSPEC3.Enable = value;
	cb_ZSPEC5.Enable = value;
	ed_ZSPEC8.Enable = value;
	cb_GBN.Enable = value;
	cb_GUBUNA.Enable = value;
	cb_GUBUNB.Enable = value;
	cb_CLDATE1_FRYY.Enable = value;
	cb_CLDATE1_FRMM.Enable = value;
	cb_CLDATE1_TOYY.Enable = value;
	cb_CLDATE1_TOMM.Enable = value;
	cb_ZZACTSS.Enable = value;
	cb_TEMNO.Enable = value;
	cb_ZZSHIP1_FRYY.Enable = value;
	cb_ZZSHIP1_FRMM.Enable = value;
	cb_ZZSHIP1_TOYY.Enable = value;
	cb_ZZSHIP1_TOMM.Enable = value;
	ed_LIFNR.Enable = value;
	btn_LIFNR.Enable = value;
	cb_AGING.Enable = value;
}

function fn_param_query() {

	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0,"KIJUN", rd_KIJUN.Value);
	ds_cond.SetColumn(0,"CDATE", ed_CDATE.Text);
	ds_cond.SetColumn(0,"BSTDK_FRYM", cb_BSTDK_FRYY.Value+cb_BSTDK_FRMM.Value);
	ds_cond.SetColumn(0,"BSTDK_TOYM", cb_BSTDK_TOYY.Value+cb_BSTDK_TOMM.Value);
	ds_cond.SetColumn(0,"VKBUR", div_vbvg.cb_frvb.Value);
	ds_cond.SetColumn(0,"VKGRP", div_vbvg.cb_frvg.Value);
	ds_cond.SetColumn(0,"ZMAN", div_vbvg.ed_sman_cd.Text);
	ds_cond.SetColumn(0,"JUDGE", cb_JUDGE.Value);
	ds_cond.SetColumn(0,"WAERK", cb_WAERK.Value);
	ds_cond.SetColumn(0,"AUART_FR", cb_AUART_FR.Value);
	ds_cond.SetColumn(0,"AUART_TO", cb_AUART_TO.Value);
	ds_cond.SetColumn(0,"GUBUNC", cb_GUBUNC.Value);
	ds_cond.SetColumn(0,"WWBLD", cb_WWBLD.Value);
	ds_cond.SetColumn(0,"JMISU", cb_JMISU.Value);

	if (length(ed_PSPID.Text) > 0) {
		ds_cond.SetColumn(0,"PSPID", LPad(ed_PSPID.Text,'0',6));
	}
	if (length(div_buyr.fr_ed_buyr_cd.Text) > 0) {
		ds_cond.SetColumn(0,"KUNNR", LPad(div_buyr.fr_ed_buyr_cd.Text,'0',10));
	}
	if (length(ed_KUNZ1.Text) > 0) {
		ds_cond.SetColumn(0,"KUNZ1", LPad(ed_KUNZ1.Text,'0',10));
	}

	if (rd_KIJUN.Value == 'A') {
		ds_cond.SetColumn(0,"ZSPEC1", cb_ZSPEC1.Value);
		ds_cond.SetColumn(0,"ZSPEC4", cb_ZSPEC4.Value);
		ds_cond.SetColumn(0,"ZSPEC3", cb_ZSPEC3.Value);
		ds_cond.SetColumn(0,"ZSPEC5", cb_ZSPEC5.Value);
		ds_cond.SetColumn(0,"ZSPEC8", ed_ZSPEC8.Text);
		ds_cond.SetColumn(0,"GBN", cb_GBN.Value);
		ds_cond.SetColumn(0,"GUBUNA", cb_GUBUNA.Value);
		ds_cond.SetColumn(0,"GUBUNB", cb_GUBUNB.Value);
		ds_cond.SetColumn(0,"CLDATE1_FRYM", cb_CLDATE1_FRYY.Value+cb_CLDATE1_FRMM.Value);
		ds_cond.SetColumn(0,"CLDATE1_TOYM", cb_CLDATE1_TOYY.Value+cb_CLDATE1_TOMM.Value);
		ds_cond.SetColumn(0,"ZZACTSS", cb_ZZACTSS.Value);
		ds_cond.SetColumn(0,"TEMNO", cb_TEMNO.Value);
		ds_cond.SetColumn(0,"ZZSHIP1_FRYM", cb_ZZSHIP1_FRYY.Value+cb_ZZSHIP1_FRMM.Value);
		ds_cond.SetColumn(0,"ZZSHIP1_TOYM", cb_ZZSHIP1_TOYY.Value+cb_ZZSHIP1_TOMM.Value);
		ds_cond.SetColumn(0,"AGING", cb_AGING.Value);
		if (length(ed_LIFNR.Text) > 0) {
			ds_cond.SetColumn(0,"LIFNR", LPad(ed_LIFNR.Text,'0',10));
		}
	} else if (rd_KIJUN.Value == 'C' or rd_KIJUN.Value == 'D') {
		var month = substr(ed_CDATE.Text,0,6);
		ds_cond.SetColumn(0,"AMT00", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),1);
		ds_cond.SetColumn(0,"AMT01", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),2);
		ds_cond.SetColumn(0,"AMT02", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),3);
		ds_cond.SetColumn(0,"AMT03", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),4);
		ds_cond.SetColumn(0,"AMT04", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),5);
		ds_cond.SetColumn(0,"AMT05", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),6);
		ds_cond.SetColumn(0,"AMT06", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),7);
		ds_cond.SetColumn(0,"AMT07", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),8);
		ds_cond.SetColumn(0,"AMT08", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),9);
		ds_cond.SetColumn(0,"AMT09", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),10);
		ds_cond.SetColumn(0,"AMT10", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),11);
		ds_cond.SetColumn(0,"AMT11", month);
		month = gfn_getAfterMonth(substr(ed_CDATE.Text,0,6),12);
		ds_cond.SetColumn(0,"AMT12", month);
	}
}

//수주유형 세팅 : 프로젝트 - 소형, 일반 
//                호기 - 금년납기, 단납기, ''
function fn_set_gubunc(domain_nm) {
	ds_GUBUNC.DeleteAll();
	ds_combo.ClearData();
	ds_combo_gubun.ClearData();
	ds_combo_gubun.AddRow();
	ds_combo_gubun.SetColumn(0, "GUBUN", 'ds_cond_GUBUNC');
	ds_cond_GUBUNC.ClearData();
	ds_cond_GUBUNC.AddRow();
	ds_cond_GUBUNC.SetColumn(0, "DOMNAME", domain_nm);

	// 서비스 호출
	nsfRequest("GUBUNC"																				// srvId
				, "sch0060/combo"																	// url
				, "ds_combo_gubun=ds_combo_gubun ds_cond_GUBUNC=ds_cond_GUBUNC ds_combo=ds_combo"	// inDsList
				, "ds_combo=ds_combo"																// outDsList
				, ""																				// strParam
				, "fn_afterQuery_combo"																// CallBackFunc
				);
}

/********************************************************************************
* 기      능   : valid 체크 
********************************************************************************/
function fn_valid_chk(gubun) {
	if (length(ed_CDATE.Text) == 0) {
		gfn_showAlert("CW10035", "기준년월 값"); //${}이/가 없습니다. //alert("기준년월 값이 없습니다.\n다시 선택해주세요.");
		gfn_SetFocus(cb_CDATE_MM,"Y"); 
		return false;
	//} else if (length(div_vbvg.cb_frvb.Value) == 0) {
	//	gfn_showAlert("CW10029", "부서"); //${}을/를 선택하세요.
	//	gfn_SetFocus(div_vbvg.cb_frvb,"Y"); 
	//	return false;
	//} else if (length(div_vbvg.cb_frvg.Value) == 0) {
	//	gfn_showAlert("CW10029", "팀"); //${}을/를 선택하세요.
	//	gfn_SetFocus(div_vbvg.cb_frvg,"Y"); 
	//	return false;
	}

	if (gubun == 'A') {
	}

	return true;
}

/********************************************************************************
* 기      능   : 기준 선택
********************************************************************************/
function rd_KIJUN_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch(strCode) {
		case "A" :	// 호기
			grd_listA.Visible = true;
			grd_listB.Visible = false;
			grd_listC.Visible = false;
			grd_listD.Visible = false;
			fn_VISIBLE(true);
			if (F_INIT) {
				ds_list.DeleteAll();
			}
			fn_set_gubunc('ZSDDGUBUNCH');	//수주유형 
			break;

		case "B" :	// 프로젝트
			grd_listA.Visible = false;
			grd_listB.Visible = true;
			grd_listC.Visible = false;
			grd_listD.Visible = false;
			fn_VISIBLE(false);
			ds_list.DeleteAll();
			fn_set_gubunc('ZSDDGUBUNCP');	//수주유형 			
			break;

		case "C" :	// 청구
			grd_listA.Visible = false;
			grd_listB.Visible = false;
			grd_listC.Visible = true;
			grd_listD.Visible = false;
			fn_VISIBLE(false);
			ds_list.DeleteAll();
			fn_set_gubunc('ZSDDGUBUNCP');	//수주유형
			break;

		case "D" :	// 수금
			grd_listA.Visible = false;
			grd_listB.Visible = false;
			grd_listC.Visible = false;
			grd_listD.Visible = true;
			fn_VISIBLE(false);
			ds_list.DeleteAll();
			fn_set_gubunc('ZSDDGUBUNCP');	//수주유형
			break;
	}
}

