* 기      능   : 기준월 선택
********************************************************************************/
function cb_CDATE_MM_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fn_CDATE();
}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 0) {
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj, nCell);
	}
}

function grd_listB_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	switch (nCell) {
		case 50 :
			// 법조치 팝업화면 OPEN
			var arg = ""
					+ " fa_frm_title= " + quote("법조치관리") 							// 팝업화면 제목
					+ " fa_kidno	= "	+ quote(ds_list.getcolumn(nRow, "PSPID"		))	// 공사번호  (프로젝트번호)
					+ " fa_kidno_t	= "	+ quote(ds_list.getcolumn(nRow, "ZSITE_NM"	))	// 현장명    (프로젝트명 )
					;

			var arr = gfn_openPopUpUrlHdel("Sch::Sch0062.xml", true, arg );	 

			if (arr != null && arr != 0)
			{
				ds_list.SetColumn(nRow, "MEKIND" , arr);	// 리턴된 법조치 종류 SET
			}
			break;
		case 51 :
			// 진행사항 팝업화면 OPEN
			var arg = ""
					+ " fa_frm_title= " + quote("진행사항관리") 						// 팝업화면 제목
					+ " fa_kidno	= "	+ quote(ds_list.getcolumn(nRow, "PSPID"		))	// 공사번호  (프로젝트번호)
					+ " fa_kidno_t	= "	+ quote(ds_list.getcolumn(nRow, "ZSITE_NM"	))	// 현장명    (프로젝트명 )
					+ " fa_kunnr	= "	+ quote(ds_list.getcolumn(nRow, "KUNNR"		))	// 거래선번호
					+ " fa_name1	= "	+ quote(ds_list.getcolumn(nRow, "KUNNR_NM"	))	// 거래선명
					; 

			var arr = gfn_openPopUpUrlHdel("Sch::Sch0063.xml", true, arg );	 

			if (arr != null && arr != 0)
			{
				ds_list.SetColumn(nRow, "INGTEXT" , arr);	// 리턴된 법조치 종류 SET
			}
			break;
	}
}

