function grd_list_OnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam,nRow,nCell,nPivotIndex)
{
	F_CHAR = true;
	F_Row = nRow;
	F_COLUMN = nCell;
}

function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if (nRow == F_Row && nCell == F_COLUMN) {
		return;
	}
	if (rd_KIJUN.Value == 'C') {
		fn_chk_AMT(nRow,grd_listC.GetCellProp("body",F_COLUMN,"ColId"));
	} else if (rd_KIJUN.Value == 'D') {
		fn_chk_AMT(nRow,grd_listD.GetCellProp("body",F_COLUMN,"ColId"));
	}
	if (F_AMT == false && F_CHAR == true) {
		gfn_SetFocusGrid(grd_listC,F_Row,F_COLUMN);
		return;
	}
}

function fn_chk_AMT(nRow,strColumnID) {
	if (SubStr(strColumnID,0,3) == 'AMT') {
		if (fn_MIJUNG(nRow) < 0) {
			F_AMT = false;
			ds_list.SetColumn(nRow,'CHK','0');
			ds_list.SetColumn(nRow,'MIJUNG',fn_MIJUNG(nRow));
			Alert('미정금액이 0보다 작습니다.\n'+(nRow+1)+'행 '+fn_AMT(strColumnID)+' 금액을 다시 입력해주세요.');
		} else {
			F_AMT = true;
			ds_list.SetColumn(nRow,'CHK','1');
		}
	}
}

function fn_SetFocusGrid(nRow,strColumnID) {
	if (rd_KIJUN.Value == 'C') {
		gfn_SetFocusGrid(grd_listC,nRow,19+ParseInt(SubStr(strColumnID,3)));
	} else if (rd_KIJUN.Value == 'D') {
		gfn_SetFocusGrid(grd_listD,nRow,24+ParseInt(SubStr(strColumnID,3)));
	}
}

function fn_AMT(strColumnID) {
	if (rd_KIJUN.Value == 'C') {
		return grd_listC.GetCellProp('Head',19+ParseInt(SubStr(strColumnID,3)),'Text');
	} else if (rd_KIJUN.Value == 'D') {
		return grd_listD.GetCellProp('Head',24+ParseInt(SubStr(strColumnID,3)),'Text');
	}
}

function fn_MIJUNG(nRow) {
	var amt = ParseCurrency(ds_list.getcolumn(nRow, 'AMT00'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT01'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT02'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT03'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT04'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT05'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT06'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT07'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT08'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT09'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT10'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT11'))
			+ ParseCurrency(ds_list.getcolumn(nRow, 'AMT12'));
	if (rd_KIJUN.Value == 'C') {
		return ParseCurrency(ds_list.getcolumn(nRow, 'MICH')) - amt;
	} else if (rd_KIJUN.Value == 'D') {
		return ParseCurrency(ds_list.getcolumn(nRow, 'MISU')) - amt;
	}
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	if(ds_error.rowcount() > 0) {   

		gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG"));   

	} else {

		// 조회된 자료가 있을 경우 
		if (ds_list.RowCount() > 0) 
		{ 
			gfn_showMsg("CI00002", ds_list.RowCount()+"");  // CI00002 : "조회를 완료했습니다.조회건수 [${}건]"

			// 통화의 존재 여부에 따라서, Summary Row의 합계 행의 갯수가 달라진다.
			var sGridSummaryFormat = "Default";
			if ( ds_list.FindRow("WAERK", "KRW") > -1 && ( ds_list.FindRow("WAERK", "USD") == -1 && ds_list.FindRow("WAERK", "JPY") == -1 ) ) {
				sGridSummaryFormat = "SummaryKrwOnly";
			} else if ( ds_list.FindRow("WAERK", "KRW") == -1 && ( ds_list.FindRow("WAERK", "USD") > -1 || ds_list.FindRow("WAERK", "JPY") ) ) {
				sGridSummaryFormat = "SummaryUsdOnly";
			}

			var objGrid;
			if ( grd_listA.Visible ) {
				objGrid = grd_listA;
			} else if ( grd_listB.Visible ) {
				objGrid = grd_listB;
			} else if ( grd_listC.Visible ) {
				objGrid = grd_listC;
			} else if ( grd_listD.Visible ) {
				objGrid = grd_listD;
			}
			objGrid.Redraw = false;
			objGrid.Format = sGridSummaryFormat;
			objGrid.BindDataset = "ds_list";
			objGrid.Redraw = true;
		} 
		else 
		{	 
			gfn_showMsg("CW00010"); 					// CW00010 : "데이터가 없습니다."
		}

		if ( rd_KIJUN.Value == "C" || rd_KIJUN.Value == "D" ) {
			fn_CDATE();
		}
	}
}

/********************************************************************************
* 기      능   : 콤보박스 조회 Callback  
********************************************************************************/
function fn_afterQuery_combo(svcId, errCode, errMsg) {

	switch(svcId) {
		case "ZSPEC1" :	// 사양1
			ds_ZSPEC1.Copy(ds_combo);
			gfn_InsertRowCombo(ds_ZSPEC1, 0, "Y", "N"); // 전체 행 추가
			cb_ZSPEC1.index = 0;
			break;

		case "ZSPEC4" :	// 사양4
			ds_ZSPEC4.Copy(ds_combo);
			gfn_InsertRowCombo(ds_ZSPEC4, 0, "Y", "N"); // 전체 행 추가
			cb_ZSPEC4.index = 0;
			break;

		case "ZSPEC3" :	// 사양3
			ds_ZSPEC3.Copy(ds_combo);
			gfn_InsertRowCombo(ds_ZSPEC3, 0, "Y", "N"); // 전체 행 추가
			cb_ZSPEC3.index = 0;
			break;

		case "ZSPEC5" :	// 사양5
			ds_ZSPEC5.Copy(ds_combo);
			gfn_InsertRowCombo(ds_ZSPEC5, 0, "Y", "N"); // 전체 행 추가
			cb_ZSPEC5.index = 0;
			break;

		case "CDATE" :	// 사양5
			//ds_CDATE.Copy(ds_combo);
			ed_CDATE.Text = ds_combo.GetColumn(0, 'CODE');
			if (length(ed_CDATE.Text) == 0) {
				ed_CDATE.DisableBkColor = '#FDA98F';
			} else {
				ed_CDATE.DisableBkColor = '#EEEBE6';
			}
			break;

		case "GBN" :	// 구분1
			ds_GBN.Copy(ds_combo);
			gfn_InsertRowCombo(ds_GBN, 0, "Y", "N"); // 전체 행 추가
			cb_GBN.index = 0;
			break;

		case "GUBUNA" :	// 구분2
			ds_GUBUNA.Copy(ds_combo);
			gfn_InsertRowCombo(ds_GUBUNA, 0, "Y", "N"); // 전체 행 추가
			cb_GUBUNA.index = 0;
			break;

		case "GUBUNB" :	// 구분3
			ds_GUBUNB.Copy(ds_combo);
			gfn_InsertRowCombo(ds_GUBUNB, 0, "Y", "N"); // 전체 행 추가
			cb_GUBUNB.index = 0;
			break;

		case "JUDGE" :	// 신용등급
			ds_JUDGE.Copy(ds_combo);
			gfn_InsertRowCombo(ds_JUDGE, 0, "Y", "N"); // 전체 행 추가
			cb_JUDGE.index = 0;
			break;

		case "ZZACTSS" :	// 설치1
			ds_ZZACTSS.Copy(ds_combo);
			gfn_InsertRowCombo(ds_ZZACTSS, 0, "Y", "N"); // 전체 행 추가
			cb_ZZACTSS.index = 0;
			break;

		case "TEMNO" :	// 설치2
			ds_TEMNO.Copy(ds_combo);
			gfn_InsertRowCombo(ds_TEMNO, 0, "Y", "N"); // 전체 행 추가
			cb_TEMNO.index = 0;
			break;

		case "AUART" :	// 문서유형
			gfn_InsertRowCombo(ds_auart_cd, 0, "Y", "N"); // 전체 행 추가
			cb_AUART_FR.index = 0;
			cb_AUART_TO.index = 0;
			break;

		case "GUBUNC" :	// 수주유형
			ds_GUBUNC.Copy(ds_combo);
			gfn_InsertRowCombo(ds_GUBUNC, 0, "Y", "N"); // 전체 행 추가
			cb_GUBUNC.index = 0;
			break;

		case "WWBLD" :	// 건물용도
			ds_WWBLD.Copy(ds_combo);
			gfn_InsertRowCombo(ds_WWBLD, 0, "Y", "N"); // 전체 행 추가
			cb_WWBLD.index = 0;
			break;

		case "AGING" :	// 납기경과 개월수
			ds_AGING.Copy(ds_combo);
			gfn_InsertRowCombo(ds_AGING, 0, "Y", "N"); // 전체 행 추가
			cb_AGING.index = 0;
			break;
	}
}

// 그리드 사이즈 조정
function Sch0060_OnSize(obj,nCx,nCy,nState)
{
	grd_listA.Top = F_TOP;
	grd_listB.Top = F_TOP;
	grd_listC.Top = F_TOP;
	grd_listD.Top = F_TOP;

	var nHeight = this.height - Shape0.Height - 13; // F_HEIGTH
	if ( nHeight <= 0 ) nHeight = F_HEIGTH;
	var nWidth  = this.width - 15;

	grd_listA.Height = nHeight;
	grd_listB.Height = nHeight;
	grd_listC.Height = nHeight;
	grd_listD.Height = nHeight;

	grd_listA.Width = nWidth;
	grd_listB.Width = nWidth;
	grd_listC.Width = nWidth;
	grd_listD.Width = nWidth;

	Shape0.Width = this.width;
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "AMT00"
		|| strColumnID == "AMT01"
		|| strColumnID == "AMT02"
		|| strColumnID == "AMT03"
		|| strColumnID == "AMT04"
		|| strColumnID == "AMT05"
		|| strColumnID == "AMT06"
		|| strColumnID == "AMT07"
		|| strColumnID == "AMT08"
		|| strColumnID == "AMT09"
		|| strColumnID == "AMT10"
		|| strColumnID == "AMT11"
		|| strColumnID == "AMT12" )
	{
		var nAmtSum = 0;
		for ( var i = 0 ; i <= 12 ; i++ )
		{
			nAmtSum += toNumber(obj.GetColumn(nRow, "AMT"+Lpad(i+"","0",2)));
		}
		obj.SetColumn(nRow, "MIJUNG", obj.GetColumn(nRow, "MISU")-nAmtSum);
	}
}
