#include "LIB::commons.js";	// 공통 스크립트 


function form_OnLoadCompleted(obj)
{
	Gfn_ResizeInit(1024,768);

	Ubi.resource = "fixed";
	Ubi.setFileURL(G_SERVER_URL+"report/IMG");
	Ubi.setJrfFileDir(G_SERVER_URL+"report/SD/");
	Ubi.setJrfFileName("SDSCL0042.jrf");

	Ubi.FontRevision = true;
	Ubi.setPageScale(100);

	Ubi.SetDataset("ds_list1", "ds_list_m");
	Ubi.SetDataset("ds_list2", "ds_list_h");
	Ubi.SetDataset("ds_list3", "ds_list_s");
	Ubi.Retrieve();
}
