//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() { 


	// trace("fn_query()호출 start");

	gfn_clearMsgBar(); 

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list); 

	// 조회조건 항목오류검증
	if (fn_valid_chk("query") == false)  return;

	// 조회조건 설정
	ds_cond.ClearData();  
	ds_cond_so_list.ClearData();  
	if (fn_param_set("query") == false)  return;
	// trace(ds_cond.SaveXML());
	// trace(ds_cond_so_list.SaveXML());

	// 서비스 호출 
	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	ds_list.ClearData();  
	ds_error.ClearData();  
	nsfRequest("query"												// srvId
				, "sco0100/find"									// url
				, "ds_cond=ds_cond ds_cond_so_list=ds_cond_so_list"	// inDsList
				, "ds_list=ds_list ds_error=ds_error"				// outDsList
				, ""												// strParam
				, "fn_afterQuery"									// CallBackFunc
				);

}


// INPUT PARAM SET
// pProcGb : 처리구분 (query:조회)
function fn_param_set(pProcGb)
{  
	var frYear  = ds_yearf_cd.GetColumn(cb_yearf.Index, "CODE");   // 수주년도
	var frMonth = ds_monthf_cd.GetColumn(cb_monthf.Index, "CODE"); // 수주월

	switch(pProcGb)
	{
		case "query" :  // 조회 시

			ds_cond.AddRow();  

			ds_cond.SetColumn(0, "FR_YM"	, frYear + frMonth					); 	// 수주년월 
			ds_cond.SetColumn(0, "FR_SO"	, trim(div_sales.ed_fromEdit.text)	);  // S/O     
			ds_cond.SetColumn(0, "FR_VB"	, trim(div_vbvg.cb_frvb.Value)		);  // 부서코드 
			ds_cond.SetColumn(0, "FR_VG"	, trim(div_vbvg.cb_frvg.Value)		);  // 팀코드     
			ds_cond.SetColumn(0, "SMAN"		, trim(div_sman.ed_sman_cd.text)	);  // 담당자  
			ds_cond.SetColumn(0, "TO_SO"	, trim(div_sales.ed_toEdit.text)	);  // S/O     
			ds_cond.SetColumn(0, "TO_VG"	, trim(div_vbvg.cb_tovg.Value)		);  // 팀코드

			// SubForm 데이타셋을 Copy해옴  : 빈행제외
			gfn_copyDs(div_sales.Ds_rtn	, ds_cond_so_list	, GC_SO_FILTER_COND)	; 	// so 범위조건

			break;  

		default: 
			break; 
	}   

	return true;

}
