// 폼 초기화 
function fn_init_form()
{
 	// 임시코딩 
	if (length(G_SESSION_ID)==0)
	{
		btn_query.Visible = true; 
		btn_excel.Visible = true;
		btn_close.Visible = true;
	}
	else
	{ 
		btn_query.Visible = false; 
		btn_excel.Visible = false;
		btn_close.Visible = false;
	}

 	// 메세지바 초기화
	gfn_clearMsgBar();  

	/* 수주년월 콤보 데이타 Load 및 기본값 SETTING */ 
	var curYear = substr(gfn_getCurrDate(),0,4); 
	fn_makeYear(curYear,-5,10);
	cb_monthf.index = parseInt(substr(gfn_getCurrDate(),4,2))-1; 

	ds_cond.ClearData();  	// 검색조건 데이타셋 초기화
	ds_list.ClearData(); 	// 검색결과 그리드데이타셋 초기화

	div_vbvg.Border = "none";
	div_sman.Border = "none"; 
	div_sales.Border = "none"; 

	// 부서/팀 검색조건 리사이징 및 스타일 적용
	div_vbvg.fn_rsetSize(280, 140, 140);	 		// 디비전사이즈, 부서콤보 사이즈, 팀콤보 사이즈
	div_vbvg.cb_frvb.Style = "sty_ipt_search_ind";	// 거래선코드 스타일 SET


	// 그리드 FIX
	gfn_grid_fix(grd_list , 6);	// 담당자명에서 fix
} 

/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum;
	var row = 0;

	for(var i=rowCnt; i>-1; i--) {
		ds_yearf_cd.AddRow();
		ds_yearf_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_yearf_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+"년");
		row++;
	}

	cb_yearf.index = eNum; 
}


function grd_list_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	var ds = object(obj.BindDataset);  

	// Enter 키이면
	if (nChar==13)
	{ 
		ds.row = ds.row + 1;  // 다음 행으로 이동
	} 
	else 
	{
		gfn_GrdCellCopyPasteReset(obj,  ds, bCtrl, nChar);
	} 
}
