/********************************************************************************
* 기      능   : 그리드 클릭
********************************************************************************/
function grd_header_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) 	{

	// 수주생성 호출 버튼 표시 여부 (해외영업팀의 경우 사용)
	if (ds_header.GetColumn(nRow, "ZPRGFLG") == "61")	{
		btn_sorlt.Visible = true;
	}else	{
		btn_sorlt.Visible = false;
	}

	// 첨부 다운로드 버튼 표시 (첨부위치 51)
	if (length(trim(obj. GetCellValue(nRow,51))) > 0)	{
		btn_filedown.Visible = true;
	}else	{
		btn_filedown.Visible = false;
	}

	fn_detail();
}

/********************************************************************************
* 기      능   : 그리드 더블 클릭시 상세 화면
********************************************************************************/
function grd_header_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	ds_arg.ClearData();
	ds_arg.AddRow();
	ds_arg.CopyRow(0, ds_header, ds_header.currow); // grd_header.CurrentRow->nRow

	var vkbur = ds_header.GetColumn(ds_header.currow, "VKBUR");
	var arg  = "fa_qtnum=" + quote(ds_header.GetColumn(nRow, "QTNUM"));
		arg += " fa_qtser=" + quote(ds_header.GetColumn(nRow, "QTSER"));
	var url = "";

	if (vkbur=="B500" || vkbur=="B600" || vkbur=="B700" || vkbur=="B900" || vkbur=="BA00" || vkbur=="BB00" ) url = "Ses::Ses0031E.xml";
	else                                url = "Ses::Ses0031.xml";

    gfn_openPopUpUrlHdel(url, true, arg);
}

