	if(ds_detail.RowCount() > 0)
		gfn_exportExcel(grd_detail, "견적등록리스트(상세)");
	else
		gfn_showAlert("CW00010");
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	if (svcId == "header") {
		fn_detail();
	} else {
		fn_destroyWait();
	}

	if(ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		gfn_openWSErrorWin(grd_list, ds_header, ds_error, "FLAG");	// error 정보 표시 
	} else {
		if(errCode != 0) gfn_showMsg("CE00001");
		else             gfn_showMsg("CI00002", ds_header.RowCount() + "");
	}
} 
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_zpstat.index = 0;
	// trace("ds_zpstat:"+ds_zpstat.SaveXML());
	// 해외모의견적상태 제외 대상: 90: 수주생성, 62: 수주실패, 61: 수주, 50: 입찰
	var findRow = -1;
	for (var i = 0 ; i < ds_rmzpstat.GetRowCount() ; i ++ ) {
	    findRow = ds_zpstat.FindRow("CODE", ds_rmzpstat.GetColumn(i, "CODE"));
	    if(findRow>-1) {
	        ds_zpstat.DeleteRow(findRow);
	    }
	}
}

