function grd_header_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	var content = grd_header.Contents;
	if( nCell == 0 ) gfn_selectAllGrid(obj,nCell,nCell,true);
	else {
		gfn_sortGrid(obj, nCell);
		grd_header.Contents = content;
	}
}

