/********************************************************************************
* 기      능   : 그리드 클릭
********************************************************************************/
function grd_header_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) 	{
	// 첨부 다운로드 버튼 표시 (첨부위치 51)
	if (length(trim(obj. GetCellValue(nRow,51))) > 0)	{
		btn_filedown.Visible = true;
	}else	{
		btn_filedown.Visible = false;
	}

	fn_detail();
}

/********************************************************************************
* 기      능   : 그리드 더블 클릭시 상세 화면
********************************************************************************/
function grd_header_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	ds_arg.ClearData();
	ds_arg.AddRow();
	ds_arg.CopyRow(0, ds_header, ds_header.currow); // grd_header.CurrentRow->nRow

    var url = "Ses::Ses0031E.xml";
    var smlqtnum = ds_header.GetColumn(ds_header.currow, "SMLQTNUM"); 
    var smlqtser = ds_header.GetColumn(ds_header.currow, "SMLQTSER");
	var qtnum    = ds_header.GetColumn(ds_header.currow, "QTNUM"); 
	var qtser    = ds_header.GetColumn(ds_header.currow, "QTSER");

    if(nCell == 1) {
	var vkbur = ds_header.GetColumn(ds_header.currow, "VKBUR");
		var arg  = "fa_qtnum=" + quote(qtnum);
			arg += " fa_qtser=" + quote(qtser);
			gfn_openPopUpUrlHdel(url, true, arg);
    } else {
        if(Length(smlqtnum) > 0) {
            ds_arg.SetColumn(0, "QTNUM", smlqtnum);
            ds_arg.SetColumn(0, "QTSER", smlqtser);
			var F_TQUOT = true;
			var vkbur = ds_header.GetColumn(ds_header.currow, "VKBUR");
			var arg  = "fa_qtnum=" + quote(smlqtnum);
				arg += " fa_qtser=" + quote(smlqtser);
			    arg += " F_SMLQTNUM=" + quote(qtnum);
			    arg += " F_SMLQTSER=" + quote(qtser);
				arg += " F_TQUOT=" + F_TQUOT;
				arg += " F_SMLYSNO=" + F_SMLYSNO;
			gfn_openPopUpUrlHdel(url, true, arg);

			//  화면 리플레시를 수행한다.
			fn_query();
        }
    }
}

/********************************************************************************
