function btn_smlreg_OnClick(obj) {
    // 모의견적서 생성
    fn_saveEgisEstimate();
}

/********************************************************************************
* 기      능   :  모의견적서 생성
********************************************************************************/
function fn_saveEgisEstimate() {
    if (ds_header.GetRowCount() == 0) {
        gfn_showAlert("CW10025","견적상세 대상을 선택하십시요.");
        return;
    }

    var smlqtnum = ds_header.GetColumn(ds_header.currow, "SMLQTNUM");
    if(Length(smlqtnum)>0) {
        gfn_showAlert("CW10025","이미 모의견적번호가 생성되었습니다.");
        return;
    }

    // 컨펌 메시지 출력
    if (gfn_showConfirm("CI00024", "모의견적 등록") == true) {
		var qtnum = ds_header.GetColumn(ds_header.currow, "QTNUM");
		var qtser = ds_header.GetColumn(ds_header.currow, "QTSER");
		var auart = ds_header.GetColumn(ds_header.currow, "AUART");
		ds_param.ClearData();
		ds_param.AddRow();
		ds_param.SetColumn(0, "QTNUM", qtnum);
		ds_param.SetColumn(0, "QTSER", qtser);
		ds_param.SetColumn(0, "AUART", auart);
		nsfRequest("fn_saveEgisEstimate", "ses0030E/saveEgisEstimate", "ds_param=ds_param", "ds_param=ds_param ds_error=ds_error", "", "fn_saveEgisEstimateAfter");
		fn_createWait("",this);
    }
}

/********************************************************************************
* 기      능   :  모의견적서 생성 후 처리
********************************************************************************/
function fn_saveEgisEstimateAfter(errCode, errMsg) {
	fn_destroyWait();
	if (ds_error.rowcount > 0) {
	    gfn_showAlert(ds_error.GetColumn(0,"ERRMSG"));
	} else {
	    //trace("ds_param xml:"+ds_param.SaveXML());
	    // 성공하면 선택
	    // 선택된 그리드에 SML_QTNUM, SML_QTSER 정보를 입력한다.
	    var smlqtnum = ds_param.GetColumn(0, "SMLQTNUM");
	    var smlqtser = ds_param.GetColumn(0, "SMLQTSER");
		var qtnum    = ds_param.GetColumn(0, "QTNUM"); 
		var qtser    = ds_param.GetColumn(0, "QTSER");

        // 견적상세(해외본팀) 파업 화면을 호출한다.
        var F_TQUOT = true;
        var arg  = "fa_qtnum=" + quote(smlqtnum);
			arg += " fa_qtser=" + quote(smlqtser);
			arg += " F_SMLQTNUM=" + quote(qtnum);
			arg += " F_SMLQTSER=" + quote(qtser);
			arg += " F_TQUOT=" + F_TQUOT;
			arg += " F_SMLYSNO=" + F_SMLYSNO;
	    var url = "Ses::Ses0031E.xml";
		gfn_openPopUpUrlHdel(url, true, arg);

		//  화면 리플레시를 수행한다.
        fn_query();
	}
}

/********************************************************************************
* 기      능   :  첨부파일 다운로드
********************************************************************************/
function btn_filedown_OnClick(obj)	{
	var nRow = ds_header.currow;

	var svrFileName = ds_header.GetColumn(nRow, "ZATTFN");
	var orgFileName = ds_header.GetColumn(nRow, "ZATTFN_OR");
	var ext         = gfn_getFileExt(ds_header.getColumn(nRow, "ZATTFN"));

	fdFileDownload.FileName =orgFileName;
	fdFileDownload.Filter = "Acrobet Reader(*.pdf)|*.pdf|CAD Files(*.dwg)|*.dwg|";
	fdFileDownload.FilterIndex = decode(ext, "pdf","0", "1");
    fdFileDownload.open();


	var idx = indexOf(fdFileDownload.FileName, ".");
	var fdFileName = fdFileDownload.FileName;

    var fileName =  decode(idx, -1, fdFileName, substr(fdFileName, 0, indexOf(fdFileName, ".")));
    var localFileName = fileName + "." + ext;

	var rMsg = tit_fileRead(httpFile
							, "SD"						//저장 디렉토리
							, svrFileName				//서버에서 전송받을 이름	
							, fdFileDownload.FilePath	//로컬 경로
							, localFileName
							);							//로컬 파일명	

	if (rMsg[0] == "E")	{
		if (length(trim(rMsg[1])) > 0)	{
			gfn_showAlert("CW10025", rMsg[1]);
		}else {
			gfn_showAlert("CW10025", "파일 다운로드에 실패하였습니다.");
		}
	}else	{
		gfn_showAlert("CW10025", "파일을 다운로드 하였습니다.");
	}
}

// 견적상세 호출 시 모의견적서 구분값을 전달한다.
function btn_detail_OnClick(obj)
{

    if (ds_header.GetRowCount() == 0) {
        gfn_showAlert("CW10025","견적상세 대상을 선택하십시요.");
        return;
    }

	var smlqtnum = ds_header.GetColumn(ds_header.currow, "SMLQTNUM"); 
	var smlqtser = ds_header.GetColumn(ds_header.currow, "SMLQTSER");
	var qtnum    = ds_header.GetColumn(ds_header.currow, "QTNUM"); 
	var qtser    = ds_header.GetColumn(ds_header.currow, "QTSER");
	var F_TQUOT = true;
	if(Length(smlqtnum) >0) {
		var vkbur = ds_header.GetColumn(ds_header.currow, "VKBUR");
		var arg  = "fa_qtnum="  + quote(smlqtnum);
			arg += " fa_qtser=" + quote(smlqtser);
			arg += " F_SMLQTNUM=" + quote(qtnum);
			arg += " F_SMLQTSER=" + quote(qtser);
			arg += " F_TQUOT=" + F_TQUOT;
			arg += " F_SMLYSNO=" + F_SMLYSNO;
		var url = "Ses::Ses0031E.xml";
		gfn_openPopUpUrlHdel(url, true, arg);
	} else {
		gfn_showAlert("CW10025","모의견적등록 처리 후 모의견적상세 버튼을 클릭하여 해외모의견적상세(해외본팀) 화면을 호출할 수 있습니다.");
	}
}

/********************************************************************************
* 기      능   : 지역콤보 변경시
********************************************************************************/
function cb_ara_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(length(trim(strCode)) == 0) 
	{
		ds_gno.Filter("LENGTH(ARA) == 0 || 1==1");
		ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");
		cb_gno.Index = 0;
		cb_user.Index = 0;
	}	
	else 
	{
		ds_gno.Filter("LENGTH(ARA) == 0 || ARA == "+quote(strCode));
		ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(strCode));
		cb_gno.Index = 0;
		cb_user.Index = 0;
	}	
}

function cb_gno_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(length(trim(strCode)) == 0) 
	{
		if(length(trim(cb_ara.Value)) > 0)
			ds_user.Filter("LENGTH(LIFNR) == 0 || ARA == "+quote(cb_ara.Value));
		else
			ds_user.Filter("LENGTH(LIFNR) == 0 || 1==1");

		cb_user.Index = 0;
	}	
	else 
	{
		ds_user.Filter("LENGTH(LIFNR) == 0 || LIFNR == "+quote(strCode));
		cb_user.Index = 0;
	}
}

// 팝업을 통해 견적번호 입력
function ufn_openPjt(obj)
{
	var arg = "fa_qtnum   = "+quote(ed_qtnum.Text);		//프로젝트번호
	var arr = gfn_openPopUpUrlHdel("Com::Com0250.xml", true, arg);			
	if (arr != null && arr != 0) {
		ed_qtnum.Text = arr[0];
	}	
}

