/********************************************************************************
* 조회 
********************************************************************************/
function fn_queryP() { 
	// 원본대상 그리드 초기화
	grdTempList.BindDataset = "";

	chk_basic.value 	= 0;
	chk_inside.value 	= 0;
	chk_outside.value 	= 0;
	chk_option.value 	= 0;
	chk_layout.value 	= 0;
	chk_other.value 	= 0;
	chk_etc.value 		= 0;

	// 필수 체크 - [견적번호]
	if(length(Trim(ed_qtnum.Text)) == 0 ) {
		//"[${}] 을/를 확인하십시요."
		gfn_showAlert("CW00022", "견적변호");
		ed_qtnum.SetFocus();
		return;
	}


	if(length(Trim(ed_qtser.Text)) == 0 ) {
		//"[${}] 을/를 확인하십시요."
		gfn_showAlert("CW00022", "차수");
		ed_qtser.SetFocus();
		return;
	}

	if(length(Trim(ed_qtseq.Text)) == 0 ) {
		//"[${}] 을/를 확인하십시요."
		gfn_showAlert("CW00022", "일련번호");
		ed_qtseq.SetFocus();
		return;
	}

	ds_resList.ClearData(); // 2013.03.21 이전 조회결과 초기화

	// Transaction
	callServiceN("query"						// srvId
				, "ses0031n/search31NP"			// url
				, "ds_resList=ds_resList"		// biz에서 받는 명, mip에서 보내는 명
				, "ds_resList=ds_resList"		// mip에서 받는 명, biz에서 보내는 명
				, "qtnum="+quote(trim(ed_qtnum.Text)) 				// strParam
					+" qtser="+quote(trim(ed_qtser.Text))
					+" qtseq="+quote(trim(ed_qtseq.Text))
				, ""							// CallBackFunc
				);
}


/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(strSvcID, nErrorCode, strErrorMag)
{ 

	if(nErrorCode < 0) {
		//  CE00001 : "처리에 실패했습니다.\n확인 후 다시 처리해 주십시오.");
		gfn_showMsg("CE00001");
	} else {
		if(strSvcID == "query") {	
			ed_temp.Text = "";

			if (ds_resList.GetRowCount() > 0) {
				gfn_showMsg("CI00002", ds_resList.RowCount() + ""); 	// CI00002 : "조회를 완료했습니다.조회건수 [${}건]"

				// 그리드 설정
				fn_sayangGridSet();
				//Checkbox1.Enable = true;
				chk_basic.Enable = true;
				chk_inside.Enable = true;
				chk_outside.Enable = true;
				chk_option.Enable = true;
				chk_layout.Enable = true;

				// 2020 브랜드
				chk_other.Enable = true;

				chk_etc.Enable = true;


				// [복사]버튼 활성여부
				btn_copy.Enable = true;
			} else {
				gfn_showAlert("해당차수가 저장되지 않았습니다. 저장후 실행해주십시오");
				// 그리드에 표시
				grdTempList.NoDataText = gridNoDataMsg;
				grdTempList.BindDataset = "";
				//Checkbox1.Enable = false;
				chk_basic.Enable = false;
				chk_inside.Enable = false;
				chk_outside.Enable = false;
				chk_option.Enable = false;
				chk_layout.Enable = false;

				// 2020 브랜드
				chk_other.Enable = true;

				chk_etc.Enable = false;

				// [복사]버튼 활성여부
				btn_copy.Enable = false;
			}

		} 
	}
}

function fn_choice(obj)
{
    if(F_TOT_IS_CHECK == true) {
		chk_basic.value = 0;
		chk_inside.value = 0;
		chk_outside.value = 0;
		chk_option.value = 0;
		chk_layout.value = 0;
		// 2020 브랜드
		chk_other.value = 0;
		chk_etc.value = 0;
        F_TOT_IS_CHECK = false;
    } else {
		chk_basic.value = 1;
		chk_inside.value = 1;
		chk_outside.value = 1;
		chk_option.value = 1;
		chk_layout.value = 1;
		// 2020 브랜드
		chk_other.value = 1;
		chk_etc.value = 1;
        F_TOT_IS_CHECK = true;
    }
	chk_basic.Click();
	chk_inside.Click();
	chk_outside.Click();
	chk_option.Click();
	chk_layout.Click();
	chk_other.Click();
	chk_etc.Click();
}

