	    btn_DetailListChk_OnClick();
	    return;
	}

	if(nCell == 0) gfn_selectGridCol(obj); 	// 컬럼 display 여부
	else gfn_sortGrid(obj, nCell);			// 그리드 정렬 처리 
}

/******************************************************
// Detail - 그리드 설정
******************************************************/
function fn_GridSet()
{
	// chk컬럼 추가하여 사용하기 위한 ds생성
	ds_copy_detail.ClearData();
	ds_copy_detail.copy(ds_detail);
	ds_copy_detail.addColumn("CHK", "STRING", 256);

	// chk컬럼 추가
	for(var i = 0; i < ds_copy_detail.getRowCount(); i++ ) {
		ds_copy_detail.setColumn(i, "CHK", 0);
	}
}

/******************************************************
// Detail - 그리드 전체선택/해제
******************************************************/
function btn_DetailListChk_OnClick(obj)
{
    if(F_DETAIL_IS_CHECK == 0) {
        F_DETAIL_IS_CHECK = 1; 
    } else {
        F_DETAIL_IS_CHECK = 0; 
    }

	grdParentList.Redraw = false;
	for(var i = 0; i < ds_copy_detail.getRowCount(); i++) {
		ds_copy_detail.setColumn(i, "CHK", F_DETAIL_IS_CHECK);
	}
	grdParentList.Redraw = true;
}

/******************************************************
// 사양 - 그리드 설정
******************************************************/
function fn_sayangGridSet()
{
	// chk컬럼, grp컬럼 추가하여 사용하기 위한 ds생성
	ds_copy_temp.ClearData();
	ds_copy_temp.copy(ds_resList);
	ds_copy_temp.addColumn("CHK", "STRING", 256);

	// chk컬럼 추가, grp컬럼 추가
	grdTempList.Redraw = false;
	for(var i = 0; i < ds_copy_temp.getRowCount(); i++ ) {
		ds_copy_temp.setColumn(i, "CHK", 0);
	}
	grdTempList.Redraw = true;

	grdTempList.BindDataset = "ds_copy_temp";
	ds_copy_temp.OnColumnChanged = "ds_copy_temp_OnColumnChanged";

	// 필터 설정
	ds_copy_temp.Filter("Substr(PRH,0,3) != 'CO_'");
}

/******************************************************
// 사양 - 그리드 전체선택/해제
******************************************************/
function btn_SayangListChk_OnClick(obj) {
    if(F_SAYANG_IS_CHECK == 0) {
        F_SAYANG_IS_CHECK = 1; 
    } else {
        F_SAYANG_IS_CHECK = 0; 
    }

	grdTempList.Redraw = false;
	for(var i = 0; i < ds_copy_temp.getRowCount(); i++) {
		ds_copy_temp.setColumn(i, "CHK", F_SAYANG_IS_CHECK);
	}
	grdTempList.Redraw = true;
}

/******************************************************
// 사양선택옵션 체크시
******************************************************/
function Checkbox_OnClick(obj,strValue) {
    var chValue = 0;
    if( strValue) {
        chValue = strValue;
    } else {
		if(F_TOT_IS_CHECK == true) {
			chValue = 1;
		} else {
			chValue = 0;
		}
    }

	grdTempList.Redraw = false;
	var grpGubun = "";
	var prh = "";

	if(obj.id == 'Checkbox1') grpGubun = "A2";			// 영업사양
	if(obj.id == 'chk_basic') grpGubun = "BASIC";		// basic
	if(obj.id == 'chk_inside') grpGubun = "INSIDE";		// inside
	if(obj.id == 'chk_outside') grpGubun = "OUTSIDE";	// outside
	if(obj.id == 'chk_option') grpGubun = "OPTION";		// option
	if(obj.id == 'chk_layout') grpGubun = "LAYOUT";		// layout
	// 2020 브랜드
	if(obj.id == 'chk_other') grpGubun = "OTHERS";		// layout
	if(obj.id == 'chk_etc') grpGubun = "ETC";		    // common	

	for ( var i = 0 ; i < ds_copy_temp.getRowCount() ; i++ ) {
		if(substr(ds_copy_temp.getColumn(i, "PRH"),0,3) == 'CO_')
			continue;

        /*
		if ( grpGubun == 'A1' ) { // 기본사양
		    prh = ds_copy_temp.getColumn(i, "PRH");
			if(prh == "EL_AUSE" || prh == "EL_AMAN" || prh == "EL_ACAPA" || prh == "EL_AOPEN" || prh == "EL_ASPD" || prh == "EL_AFQ" || prh == "EL_ASTQ" ) {
                ds_copy_temp.OnColumnChanged = "";
				if(strValue == 0 ) ds_copy_temp.setColumn(i, "CHK", 0);
				else ds_copy_temp.setColumn(i, "CHK", 1);
				ds_copy_temp.OnColumnChanged = "ds_copy_temp_OnColumnChanged";
			}
		}
		*/
		// 영업사양
		if ( grpGubun == 'A2' ) {
			if ( substr(ds_copy_temp.getColumn(i, "PRH"),3,1) == 'A'
			 || substr(ds_copy_temp.getColumn(i, "PRH"),3,1) == 'B' 
			 || substr(ds_copy_temp.getColumn(i, "PRH"),3,1) == 'C' 
			 || substr(ds_copy_temp.getColumn(i, "PRH"),3,1) == 'D') {
			    ds_copy_temp.setColumn(i, "CHK", chValue);
			}
		}

		if(grpGubun == 'BASIC' || grpGubun == 'INSIDE' || grpGubun == 'OUTSIDE' || 
		   grpGubun == 'OPTION' || grpGubun == 'LAYOUT' || grpGubun == 'OTHERS' || grpGubun == 'COMMON') {
			if(IndexOf(ds_copy_temp.getColumn(i, "ATKLA"), grpGubun) > -1) {
			    ds_copy_temp.setColumn(i, "CHK", chValue);
			}
		}

        // 용도, 인승,  열림, 속도, 층수 특성값 복사
		if(grpGubun == 'ETC') {
		    prh = ds_copy_temp.getColumn(i, "PRH");
			if(prh == "EL_AUSE" || prh == "EL_AMAN" || prh == "EL_ACAPA" || prh == "EL_AOPEN" || prh == "EL_ASPD" || prh == "EL_AFQ" || prh == "EL_ASTQ" ) {
			    ds_copy_temp.setColumn(i, "CHK", chValue);
			}
		}
	}

	grdTempList.Redraw = true;
}

/******************************************************
// 복사
******************************************************/
function btn_copy_OnClick(obj)
{
	if(ds_copy_detail.CaseCount("CHK=='1'") < 1 || ds_copy_temp.CaseCount("CHK=='1'") < 1) {
		// [${}]과 [${}]이 존재하는지 확인바랍니다.
		var arr = ["복사할 대상","복사할 원본"];
		gfn_showAlert("CW11001", arr);
		return false;
	}

	fn_createWait("",this);

	// 사양그리드 redraw 설정
	var grdArry = ["parent.tab.template.div_template.grd_template1", "parent.tab.template.div_template.grd_template2",
				   "parent.tab.template.div_template.grd_template3", "parent.tab.template.div_template.grd_template4",
				   "parent.tab.template.div_template.grd_template5", "parent.tab.template.div_template.grd_template0"];

	for(var i = 0; i < grdArry.length; i++) {
		eval(grdArry[i]).Redraw = false;
	}

	var searchRow, baseCnt = 0, changCnt = 0, notCnt = 0;
	ds_template.UnFilter();

	/*
		1. 대상 그리드 건수 체크
		2. 대상 그리드 중 체크 된 행 확인
		3. 복사 그리드 건수 체크
		4. 복사 그리드 중 체크 된 행 확인 및 대상건 증가
		5. 복사 하면서 대상 그리드 flag 변경.
	*/
    var moditp;
    var disptp;
    var searchRow = 0;
    var dsOrgValue;
    var dsFlag;
	var orgAtkla;
    var orgPrhname;
    var orgPrh;
    var orgPrd;
    var orgDisptp;
    var orgmoditp;
    var brndcd;
    var brndseq;
    var orgCnt;
    var isCopy = false;

    var copyRslt;
    var rsltDesc;

    ds_copy_result.ClearData(); 

	//trace("ds_template:"+ds_template.SaveXML());
	for(var i = 0; i < ds_copy_detail.rowcount; i++) {
		if(ds_copy_detail.GetColumn(i, "CHK") == "1") {

			brndcd = ds_copy_detail.GetColumn(i, "BRNDCD");
			brndseq = ds_copy_detail.GetColumn(i, "BRNDSEQ");
			// 영업특성값을 조회한다.
			fn_sayangPrdList(brndseq, brndcd);

			for(var j = 0; j < ds_copy_temp.rowcount; j++) {
				if(ds_copy_temp.GetColumn(j, "CHK") == "1") {
					copyRslt = "";
					baseCnt++;
					orgAtkla   	= ds_copy_temp.GetColumn(j, "ATKLA");
					orgPrh    	= ds_copy_temp.GetColumn(j, "PRH");
					orgPrd    	= ds_copy_temp.GetColumn(j, "PRD");
			        orgCnt    	= ds_copy_temp.GetColumn(j, "CNT");
			        orgPrhname	= ds_copy_temp.GetColumn(j, "PRHNAME");

					// trace("orgAtkla : " +orgAtkla+ " orgPrh:"+orgPrh+", orgPrd:"+orgPrd);		        
					searchRow = ds_template.SearchRow("QTSEQ=='" + ds_copy_detail.GetColumn(i, "QTSEQ") + "' && PRH=='"+orgPrh+"'");
					if(searchRow > -1) {
						disptp = ds_template.GetColumn(searchRow, "DISPTP");
						moditp = ds_template.GetColumn(searchRow, "MODITP");
				        orgDisptp = ds_copy_temp.GetColumn(j, "DISPTP");
				        orgmoditp = ds_copy_temp.GetColumn(j, "MODITP");
          				//trace("orgPrd:"+orgPrd+", orgCnt:"+orgCnt+", disptp:"+disptp+", moditp:"+moditp+", orgDisptp:"+orgDisptp+", orgmoditp:"+orgmoditp);         				
					    if(moditp == "X" && disptp == "X" && orgDisptp == "X" && orgmoditp == "X") {
					        if(orgPrh == "EL_AUSE" || orgPrh == "EL_AMAN" || orgPrh == "EL_ACAPA" || orgPrh == "EL_AOPEN" || orgPrh == "EL_ASPD" || orgPrh == "EL_AFQ" || orgPrh == "EL_ASTQ" ) {
								searchRow = ds_detail.SearchRow("QTSEQ=='" + ds_copy_detail.GetColumn(i, "QTSEQ") + "'");
								switch (orgPrh) {
									case "EL_AUSE" : orgPrh = "ZUSE";   break;
									case "EL_ACAPA": orgPrh = "ZACAPA"; break;
									case "EL_ASTQ" : orgPrh = "ZLEN";   break;
									case "EL_AOPEN": orgPrh = "ZOPN";   break;
									case "EL_AMAN" : orgPrh = "TYPE1";  break;
									case "EL_ASPD" : orgPrh = "TYPE2";  break;
									case "EL_AFQ"  : orgPrh = "TYPE3";  break;
									default       : break;
								}
								ds_detail.SetColumn(searchRow, orgPrh, orgPrd);
							    changCnt++;
							} else {
							    isCopy = false;
							    if(orgCnt != "0") {
							        isCopy = true;
							    } else {
							        isCopy = fn_isBrndPrd(brndseq, brndcd, orgPrh, orgPrd);
							    }
						        // 브랜드별로 영업사양 특성값 존재 여부 체크
								if(isCopy) {
									dsOrgValue = ds_template.GetOrgColumn(searchRow, "PRD");
									dsFlag = ds_template.GetColumn(searchRow, "FLAG");
									//trace("orgPrd:"+orgPrd+", dsOrgValue:"+dsOrgValue+", dsFlag:"+dsFlag);
									if( dsOrgValue == null && orgPrd == '' ) {
									} else if( dsOrgValue == '' && orgPrd == null ) {
									} else {
										ds_template.SetColumn(searchRow, "PRD", orgPrd);
										if( dsOrgValue != orgPrd ) {
											if(dsFlag == "I") {
												ds_template.SetColumn(searchRow, "FLAG", "I");
											} else {
												ds_template.SetColumn(searchRow, "FLAG", "U");
											}
										} else if( dsOrgValue == orgPrd ) {
											if(flag == "U") {
												ds_template.SetColumn(searchRow, "FLAG", "");
											}
										}
									}
									changCnt++;
								} else {
									// 브랜드 영업특성값이 존재하지 않으면 영업특성이 존재해도 복사하지 않는다.
									copyRslt = "1";
									notCnt++;
								}
							}
					    } else {
							copyRslt = "2";
						    notCnt++;
					    }
					} else {
						copyRslt = "3";
						notCnt++;
					}

					if ( copyRslt != "" ) {

						switch (copyRslt) {
							case "1" : rsltDesc = "특성값 부재";   			  	break;
							case "2" : rsltDesc = "출력여부, 수정여부 CHECK"; 	break;
							case "3" : rsltDesc = "특성코드 부재";   			break;
							default       : break;
						}

						iRow = ds_copy_result.AddRow();			        
						ds_copy_result.SetColumn(iRow, "ATKLA", 	orgAtkla  	);
						ds_copy_result.SetColumn(iRow, "PRH", 		orgPrh  	); 
						ds_copy_result.SetColumn(iRow, "PRHNAME", 	orgPrhname 	);
						ds_copy_result.SetColumn(iRow, "PRD", 		orgPrd  	);						
						ds_copy_result.SetColumn(iRow, "ORGDISPTP", orgDisptp  	);
						ds_copy_result.SetColumn(iRow, "ORGMODITP", orgModitp  	);
						ds_copy_result.SetColumn(iRow, "BRNDCD", 	brndcd  	);
						ds_copy_result.SetColumn(iRow, "BRNDSEQ", 	brndseq  	);															
						ds_copy_result.SetColumn(iRow, "DISPTP", 	disptp  	);
						ds_copy_result.SetColumn(iRow, "MODITP", 	moditp  	);
						ds_copy_result.SetColumn(iRow, "RLSTDESC", 	rsltDesc	);

						copyRslt = "";
					}
				}
			}
		}
	}

	// ds copy 진행
	parent.fn_template("", ds_detail.rowposition);

	// 사양복수 후 공통항목 재처리
	parent.fn_afterItemCopy();

	for(var i = 0; i < grdArry.length; i++) {
		eval(grdArry[i]).Redraw = true;
	}

	fn_destroyWait();

	// 결과 보고
	var arr = [baseCnt,changCnt,notCnt];
	gfn_showAlert("CI10004", arr);	// 총건수:[${}]건 중 반영된건수:[${}] \n 실패한 특성:[${}]

	//trace(" ds_copy_result row count : "+ds_copy_result.rowcount());
	//trace(" ds_copy_result : "+ds_copy_result.SaveXML());

	if (notCnt != 0) {
		fn_openLogWin("grd_logList", ds_copy_temp, ds_copy_result, "FLAG");
	}
}

/******************************************************
// 엔터키 입력 시 조회되도록 함
******************************************************/
function ed_qtserseq_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam) {
	if(nChar == 13) fn_queryP();
}

