function ed_temp_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
	if(nChar == "13") {
		if(length(obj.text) < 1) {
			grdTempList.Redraw = false;
			grdTempList.SetCellProp("body", 1, "bkColor", "");
			grdTempList.SetCellProp("body", 1, "bkColor2", "User Color 22");
			grdTempList.Redraw = true;
		} else {
			grdTempList.Redraw = false;
			grdTempList.SetCellProp("body", 1, "bkColor", "expr:iif(IndexOf(PRHNAME, ed_temp.Text) < 0, '', 'red')");
			grdTempList.SetCellProp("body", 1, "bkColor2", "expr:iif(IndexOf(PRHNAME, ed_temp.Text) < 0, 'User Color 22', 'red')");
			grdTempList.Redraw = true;
		}
	}	
}

function ed_qtser_OnKillFocus(obj) {
	var objText;
	switch(obj) {
	case ed_qtser:
	case ed_qtseq:
		for(var i=0; i<obj.MaxLength; i++) {
			objText += '0';
		}

		for(var i=0; i<obj.MaxLength; i++) {
			if(Substr(obj.Text,i,1) == '')
				continue;

			objText += Substr(obj.Text,i,1);
		}

		obj.Text = Substr(objText, Length(objText)-obj.MaxLength, obj.MaxLength);
		break;

	default:
	}
}
/******************************************************
*  WebService 처리시 발생한 에러 정보 표시
* @param  grdObj 처리 그리드 객체
* @param  dsObj 데이터셋 객체
* @param  errDsObj 에러 정보 데이터셋 객체
* @param  flagColNm 플래그 컬럼명
* @return
******************************************************/
function fn_openLogWin(grdObj, dsObj, logDsObj, flagColNm) {
	var grdNm = "";

	if ( type( grdObj ) == "STRING") {
		grdNm = grdObj;
	} else {
		grdNm = grdObj.id;
	}

	var dsNm = dsObj.id;
	var logDsNm = logDsObj.id;

	var arg = "fa_flagNm=" + quote( flagColNm);
		arg += " fa_grdName=" + quote(grdNm);
		arg += " fa_dsName=" + quote( dsNm);
		arg += " fa_logDsName=" + quote( logDsNm );

	gfn_openPopupUrl("COM::Com0273.xml", true, arg);
}
