function fn_OnLoadCompleted(obj) {

	gfn_initForm(true);	// Form 로드 이벤트 맨 첫줄에 기술

	var codeArr = ["SD107", "BS102", "BS403", "VKBUR", "VKGRP", "CS115", "SPART", "WAERK", "SD115", "SD122"]; 	//코드콤보 설정
	var dsObjArr = [ds_zprgflg, ds_sukum, ds_zbdtyp, ds_vkbur, ds_vkgrp, ds_zregn1, ds_spart_cd, ds_waerk, ds_fa_byrgbn, ds_zurate];
	var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_SELECT]; //[G_CODE_NONE,G_CODE_NONE,G_CODE_NONE]; [G_CODE_ALL];
	var oderTypeArr = ["DTEXT1"];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

    // 타이틀명 변경
    if(F_SMLYSNO == "Y") {
        F_FRM_TITLE = "해외모의견적상세(해외본팀)";
    }

	if(G_USER_ID == '2022753'
	|| G_USER_ID == '2019329'
	|| G_USER_ID == '2026953'
	|| G_USER_ID == '2026996'
	|| G_USER_ID == '2022281'
	|| G_USER_ID == '2028441'
	|| G_USER_ID == '2023440'
	|| G_USER_ID == '2029111'
	|| G_USER_ID == '2018144'
	) {
		div_btn.fn_loadUseBtn("22,30,40,45", F_FRM_TITLE, "", "");
		div_btn.btn_recost.OnClick = "btn_recost_OnClick";
		div_btn.btn_recost.Text = "병렬산출";
		div_btn.btn_recost.Visible = btn_count.Visible;

		destroy('lds_cpc');
		create('dataset','lds_cpc');
		lds_cpc.AddColumn('code');
		lds_cpc.AddColumn('codeName');

			lds_cpc.AddRow();
			lds_cpc.SetColumn(lds_cpc.GetRowCount()-1,0,(1));
			lds_cpc.SetColumn(lds_cpc.GetRowCount()-1,1,(1));
		for(var idx=0; idx < 5; idx++) {
			lds_cpc.AddRow();
			lds_cpc.SetColumn(lds_cpc.GetRowCount()-1,0,(idx+2));
			lds_cpc.SetColumn(lds_cpc.GetRowCount()-1,1,(idx+2));
		}

		tab.main.div_header.cb_cpc.CodeColumn = 'code';
		tab.main.div_header.cb_cpc.DataColumn = 'codeName';
		tab.main.div_header.cb_cpc.InnerDataset = 'lds_cpc';
		tab.main.div_header.cb_cpc.Index = 1;
		tab.main.div_header.cb_cpc.Visible = true;
	} else {
		div_btn.fn_loadUseBtn("30,40,45", F_FRM_TITLE, "", "");
	}
	div_btn.btn_excel.domainID = "";
	div_btn.btn_excel.imageID  = "btn_comm_print_k";
	div_btn.btn_excel.Text     = "내역";
	tab.main.div_header.btn_specsheet.Visible = true;

	// 2020 브랜드 그룹데이터를 조회한다.
	fn_findZsdt1143List();

	fn_resize(); // 2013.02.07 사양 넓게 보이기 위해 사이즈 조정
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

