}

function fn_visible(str) {

	var searchRow;
	switch (str) {
		case "estimate" : searchRow = ds_zprgflg.searchRow("CODE =="+quote("34")); // 견적가 승인요청
						  ds_header.SetColumn(0, "ZPRGFLG", "34");
						  fn_zprgflg(); break;
		default : break;
	}

	tab.main.div_header.cb_zprgflg.Index=searchRow;
}
function valid_egisSpecType(egis_estno, egis_spcty) {
	var valid = true;

	if(egis_estno > '' && egis_spcty <> "0" && egis_spcty <> "1" && egis_spcty <> "2") {
		gfn_showAlert("EGIS_SPEC_TYPE 값이 존재하지않습니다.");	
		valid = flase;
	}

	return valid;
}
function decide_zflg(egis_estno, egis_spcty) {
	var zflg;

	if(egis_estno > '')
		zflg = egis_spcty;
	else
		zflg = 'Q';

	return zflg;
}
function btn_duty_OnClick(obj) {
    var sndSys = "PDM"; 
	var egis_estno = ds_detail.GetColumn(ds_detail.row, "EGIS_EST_NO");
	var egis_spcty = ds_detail.GetColumn(ds_detail.row, "EGIS_SPEC_TYPE");

	if(!valid_egisSpecType(egis_estno, egis_spcty)) {
		return;
	}

	btn_count.Visible = false;	// 2013.02.18 ★★★★★
	div_btn.btn_recost.Visible = btn_count.Visible;
	tab.template.div_template.btn_cost.Visible = false;
	ds_error.ClearData();

    // 2020 브랜드
    sndSys = ds_detail.GetColumn(ds_detail.currow, "SNDSYS");
    plm_ds_log.ClearData();
    ds_log.ClearData();

    if(sndSys == "PLM") {
        // PLM 연동 처리
        fn_singlePlmDuty("DUTY");
   		fn_destroyWait();
		if(plm_ds_log.getRowCount() > 0) {
			fn_openPlmLogWin("grd_logList", ds_template, plm_ds_log, "FLAG");
			return false;
		}
    } else {
		ds_cond.ClearData();
		ds_cond.AddRow();
		ds_cond.SetColumn(0, "MANDT"   , G_MANDT         );
		ds_cond.SetColumn(0, "BLOCKGBN", tab.template.div_template.cb_block.value);
		ds_cond.SetColumn(0, "ZFLG", decide_zflg(egis_estno, egis_spcty));

		var cond = "BLOCKGBN=" + tab.template.div_template.cb_block.value;

		fn_duty();
		//=========================================================================================
		//  기능ID   : UF003  
		//  수정내역 : 영업사양 종속성체크 호출 변경
		//             duty/genDuty >>> dutyN/genSpecDutySingleMo
		//=========================================================================================	
		nsfRequest("queryDuty"
				   , "dutyN/genSpecDutySingleMo"
				   , "ds_cond=ds_cond ds_template1=ds_template_add"
				   , "ds_template_add=ds_template1 ds_error=ds_error"
				   , cond
				   , "fn_afterQuery");
		fn_createWait("",this);     // 처리중입니다. 메시지 처리
	}
}

function fn_duty() {

	var j = 0;
	var obj_template;

	ds_template_add.ClearData();

	var qtseq = ds_detail.GetColumn(ds_detail.currow, "QTSEQ");
	var nRow = 0;
	for (var a=0; a<=6; a++) {

		switch (a) {
			case 0	: obj_template = object(ds_template0.ID); break;
			case 1	: obj_template = object(ds_template1.ID); break;
			case 2	: obj_template = object(ds_template2.ID); break;
			case 3	: obj_template = object(ds_template3.ID); break;
			case 4	: obj_template = object(ds_template4.ID); break;
			case 5	: obj_template = object(ds_template5.ID); break;
			case 6	: obj_template = object(ds_template6.ID); break;
			default : break;
		}

        // 2020 브랜드 입력화 처리 PRH", "OPT_GROUP_TITLE
		for (var i=0; i< obj_template.GetRowCountNF(); i++) {
		    if( obj_template.GetColumnNF(i, "QTSEQ") == qtseq && obj_template.GetColumnNF(i, "PRH") != "OPT_GROUP_TITLE") {
				nRow = ds_template_add.AddRow();
				ds_template_add.SetColumn(nRow, "FLAG"   , Trim(obj_template.GetColumnNF(i, "FLAG"   )));
				ds_template_add.SetColumn(nRow, "MANDT"  , Trim(obj_template.GetColumnNF(i, "MANDT"  )));
				ds_template_add.SetColumn(nRow, "ATKLA"  , Trim(obj_template.GetColumnNF(i, "ATKLA"  )));
				ds_template_add.SetColumn(nRow, "PRH"    , Trim(obj_template.GetColumnNF(i, "PRH"    )));
				ds_template_add.SetColumn(nRow, "PRHNAME", Trim(obj_template.GetColumnNF(i, "PRHNAME")));
				ds_template_add.SetColumn(nRow, "PRD"    , Trim(obj_template.GetColumnNF(i, "PRD"    )));
				ds_template_add.SetColumn(nRow, "ZRMK"   , Trim(obj_template.GetColumnNF(i, "ZRMK"   )));
				ds_template_add.SetColumn(nRow, "QTNUM"  , Trim(obj_template.GetColumnNF(i, "QTNUM"  )));
				ds_template_add.SetColumn(nRow, "QTSER"  , Trim(obj_template.GetColumnNF(i, "QTSER"  )));
				ds_template_add.SetColumn(nRow, "QTSEQ"  , Trim(obj_template.GetColumnNF(i, "QTSEQ"  )));
				ds_template_add.SetColumn(nRow, "PRSEQ"  , Trim(obj_template.GetColumnNF(i, "PRSEQ"  )));
				ds_template_add.SetColumn(nRow, "ATFOR"  , Trim(obj_template.GetColumnNF(i, "ATFOR"  )));	// 2013.01.09 사양 입력 구분
				ds_template_add.SetColumn(nRow, "DISPTP"  , Trim(obj_template.GetColumnNF(i, "DISPTP"  )));
				ds_template_add.SetColumn(nRow, "MODITP"  , Trim(obj_template.GetColumnNF(i, "MODITP"  )));
		    }
		}
	}
}

function fn_query_detail(zprgflg, arg) {

	if (zprgflg != null) {
		ds_header.SetColumn(0, "ZPRGFLG", zprgflg); // 32 원가산출, 41 원가팀의뢰
		nsfRequest("saveHeaderFlag", "ses0031n/updateHeaderFlag", "ds_header=ds_header", "ds_header=ds_header ds_error=ds_error", "TQUOT="+F_TQUOT+" SMLYSNO="+F_SMLYSNO, "fn_afterSave");
	}
	//2013.02.06 저장된정보를 Query함 -> Ses0171에서 값을 받아서 처리되도록 변경 
	//nsfRequest("queryDetail", "ses0030/detail", "ds_cond=ds_header ds_detail=ds_detail", "ds_detail=ds_detail ds_error=ds_error", "", "");
	// 2018.05.02  F_CLICKED_ROW -> F_CLICKED_ROW_TR 수정, F_CLICKED_ROW변수 저장후 -1로 초기화 되어 Ses0171.xml Return 오류 발생 보안 by mj.Shin 
	ds_detail.SetColumn(F_CLICKED_ROW_TR, "ZUAM", 	arg[0]);		//단가
	ds_detail.SetColumn(F_CLICKED_ROW_TR, "ZUAMS",	arg[1]);		//단가(상해)
	ds_detail.SetColumn(F_CLICKED_ROW_TR, "ZCOST", 	arg[2]);		//원가
	ds_detail.SetColumn(F_CLICKED_ROW_TR, "ZEAM", 	arg[3]);		//견적가
	ds_detail.SetColumn(F_CLICKED_ROW_TR, "SHANG", 	arg[4]);		//시행율
	ds_detail.SetColumn(F_CLICKED_ROW_TR, "ZURATE", arg[5]);		//상해간접비율
}

function fn_zcost_notnull() {

	var zcost;
	var nullCnt = 0;

	for (var i=0; i<ds_detail.GetRowCount(); i++) {
		zcost = Round(ds_detail.GetColumn(i, "ZCOST"), 0);
		if (zcost == 0) nullCnt++;
	}

	if (nullCnt == 0) return true;
	else              return false;
}

function fn_duty_enable_set(nRow) {

	var obj    = tab.template.div_template;
	var shangh = ds_detail.GetColumn(nRow, "SHANGH");

	if (shangh=="상해" || shangh=="상해_E") {
		obj.cb_block.Visible = false; obj.btn_duty.Visible = false;
		obj.btn_depend.Visible = false;
		obj.st_gtype.Left  = 2;   obj.ed_gtype.Left=194; obj.ed_type1.Left=277; obj.ed_type2.Left=360; obj.ed_type3.Left=442;
		obj.st_gtype.Width = 190; 
		btn_countRequest.Visible = false; 
		btn_count.Visible = false;
		div_btn.btn_recost.Visible = btn_count.Visible;
		tab.template.div_template.btn_cost.Visible = false;
	}
}

