function fn_OnLoadCompleted(obj) {
	if(tquot == true)
		F_TQUOT = tquot;

	gfn_initForm(false);// Form 로드 이벤트 맨 첫줄에 기술 
//	gfn_translation(obj, G_LANG);
	fn_init_form(); 	// 폼 초기화

	if (G_LANG == "en") F_FRM_TITLE = "Order Plan";

	div_btn.fn_loadUseBtn("1", F_FRM_TITLE, "", "");
}

/********************************************************************************
* 기      능   : 지급예정년 계산(현재년도,당해년도에서 뺄 년수, 당해년도에서 더할 년수
********************************************************************************/
function fn_makeYear(curYear,sNum,eNum) {
	var rowCnt = eNum-sNum;
	var row = 0;
	var strYear;

	if (G_LANG == "en") strYear = " YR";
	else                strYear = "년";

	for(var i=rowCnt; i>-1; i--) {
		ds_year_cd.AddRow();
		ds_year_cd.SetColumn(row,"CODE",parseInt(curYear)+i+parseInt(sNum));
		ds_year_cd.SetColumn(row,"CODE_NAME",(parseInt(curYear)+i+parseInt(sNum))+ strYear);
		row++;
	}

	cb_year.index  = eNum;
	cb_month.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	var zpym = SubStr(cb_year.Text,0,cb_year.Text.Length(cb_year.Text)-1) + Lpad(SubStr(cb_month.Text,0,cb_month.Text.Length(cb_month.Text)-1),"0",2);
	zpym = Replace(zpym, " Y", "");
	zpym = Replace(zpym, " M", "");

	ds_cond.ClearData();
	ds_cond.AddRow();

	switch (G_VEND_WGBN) {
		case "E" : ds_cond.setColumn(0, "ZAGNT" , G_VEND_CODE             ); break; //  로컬테스트인 경우 주석처리
		default  : ds_cond.SetColumn(0, "ZKUNNR", div_sman.ed_sman_cd.Text); break;
	}

	ds_cond.setColumn(0, "MANDT", G_MANDT);
	ds_cond.SetColumn(0, "ZPYM" , zpym   );
	ds_cond.SetColumn(0, "KUNNR", gfn_SetStrPad(trim(div_kunnr.ed_buyr_cd.Text), "0", 10, "L"));	// 기존 소스에서 고객코드 0000를 삭제하고 제공하여 추가

	nsfRequest("query", "ses0032/search", "ds_cond=ds_cond ds_list=ds_list", "ds_list=ds_list ds_error=ds_error", "", "fn_afterQuery");
    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {

	var curYear = substr(gfn_getCurrDate(),0,4);
	fn_makeYear(curYear,-4,1);
	cb_month.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	cb_year.SetFocus();

	if (branch == "branch" && G_LANG == "en") {

		div_kunnr.ed_buyr_cd.Width = 72; div_kunnr.ed_buyr_cd.Enable  = false; div_kunnr.ed_buyr_cd.Text = G_VEND_CODE;
		div_kunnr.ed_buyr_nm.Left  = 93; div_kunnr.ed_buyr_nm.Enable  = false; div_kunnr.ed_buyr_nm.Text = G_VEND_NAME;
		div_kunnr.btn_popBuyr.Left = 73; div_kunnr.btn_popBuyr.Enable = false;

	} else {
		// 2013.01.14 고객코드의 0000을 제거
		div_kunnr.ed_buyr_cd.Text = kunnr;

		if (IsExistVar("kunnr", true))
			div_kunnr.fn_query_buyr();	// 2013.01.14 조회 처리
	}

	if (popupYn == "Y") {
		div_kunnr.ed_buyr_cd.Enable  = false;
		div_kunnr.ed_buyr_nm.Enable  = false;
		div_kunnr.btn_popBuyr.Enable = false;
	}

	if (G_LANG == "en") {

		st_zpym.Text  = "contract "; // 수주년월
		st_sman.Text  = "PIC ";      // 담당자
		st_kunnr.Text = "customer number "; st_kunnr.WordWrap = true; // 고객번호

		var monthName;
		for (var i=0; i<ds_month_cd.GetRowCount(); i++) {
			monthName = ds_month_cd.GetColumn(i, "CODE_NAME");
			monthName = replace(monthName, "월", " MO");
			ds_month_cd.SetColumn(i, "CODE_NAME", monthName);
		}

		var content = grd_list.Contents;

		content = Replace(content, "수주계획번호"         , "order&#10;plan number");
		content = Replace(content, "계획년월"             , "plan year&#10;/month");
		content = Replace(content, "제품군"               , "product");
		content = Replace(content, "판매문서&#10;유형"    , "sales&#10;document&#10;type");
		content = Replace(content, "자재번호"             , "material&#10;number");
		content = Replace(content, "부서"                 , "DPT");
		content = Replace(content, "팀"                   , "team");
		content = Replace(content, "담당자명"             , "PIC Name");
		content = Replace(content, "담당자"               , "PIC");
		content = Replace(content, "기종"                 , "model");
		content = Replace(content, "대수"                 , "units");
		content = Replace(content, "고객번호"             , "customer&#10;number");
		content = Replace(content, "고객명"               , "customer&#10;name");
		content = Replace(content, "공사명"               , "proj. name");
		content = Replace(content, "설치지역"             , "site");
		content = Replace(content, "단납기&#10;구분"      , "Multi");
		content = Replace(content, "국내&#10;상해"        , "Korea/&#10;Shanghai");
		content = Replace(content, "중계무역&#10;구분"    , "intermediary&#10;trade");
		content = Replace(content, "수주예상액"           , "ex-amount");
		content = Replace(content, "예상&#10;시행율"      , "ex-p&#10;rate");
		content = Replace(content, "통화"                 , "currency");
		content = Replace(content, "납기예정일"           , "ex-shipment");
		content = Replace(content, "비고"                 , "remarks");
		content = Replace(content, "수주&#10;가능성"      , "order&#10;feasibility");
		content = Replace(content, "수주&#10;결과"        , "order&#10;result");
		content = Replace(content, "수주금액"             , "order&#10;amount");
		content = Replace(content, "시행율"               , "P rate");
		content = Replace(content, "이동계획&#10;반영여부", "moving&#10;plan&#10;status");
		content = Replace(content, "판매문서"             , "sales&#10;document");
		content = Replace(content, "사업계획번호"         , "busi-plan&#10;number");

		grd_list.Contents = content;

		div_sman.ed_sman_cd.Enable  = false;
		div_sman.btn_popSman.Enable = false;

		if (parent.F_FRM_ID == "Ses0051") {
			div_kunnr.ed_buyr_cd.Enable  = false;
			div_kunnr.btn_popBuyr.Enable = false;
			div_kunnr.ed_buyr_nm.Enable  = false;
		}
	}
}

