}

/********************************************************************************
* 기      능   : 그리드 더블 클릭시 상세 화면
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	ds_arg.ClearData();
	ds_arg.AddRow();
	ds_arg.CopyRow(0, ds_list, grd_list.CurrentRow);

	var auart = Trim(ds_list.GetColumn(grd_list.CurrentRow, "AUART"));
	var opendt = Trim(ds_list.GetColumn(grd_list.CurrentRow, "OPENDT"));

	if(opendt == "Y") {
		gfn_showAlert("견적등록/수정/삭제는 오픈된 E-GIS시스템에서 가능합니다.");	
		return;
	}

	if ( auart == "") {
		alert("판매문서 유형이 없습니다.");	return;
	} else {
		if (popupYn == "Y") {

			var arr = [ ds_list.GetColumn(ds_list.row, "SHANGH" ) // 국내상해구분
					  , ds_list.GetColumn(ds_list.row, "MATNR"  ) // 자재번호
					  , ds_list.GetColumn(ds_list.row, "RTYPE"  )
					  , ds_list.GetColumn(ds_list.row, "TYPE1"  )
					  , ds_list.GetColumn(ds_list.row, "TYPE2"  )
					  , ds_list.GetColumn(ds_list.row, "TYPE3"  )
					  , ds_list.GetColumn(ds_list.row, "ZNUMBER")
					  , ds_list.GetColumn(ds_list.row, "SONNR"  )  // 수주계획번호
					  , ds_list.GetColumn(ds_list.row, "SID"    )];// 승강기번호

			close(arr);	// 배열을 연동화면으로 전송하고 팝업화면을 닫는다.
		} else {
			var arg  = "fa_sonnr=" + quote(ds_list.GetColumn(ds_list.currow, "SONNR"));
				arg += " flag="+quote("I");
				arg += " TQUOT="+F_TQUOT;
			var url   = "";
			var vkbur = ds_list.GetColumn(ds_list.row, "VKBUR");

			switch (branch) {
				case "head":
					if (vkbur=="B500" || vkbur=="B600" || vkbur=="B700"|| vkbur=="B900" || vkbur=="BA00"|| vkbur=="BB00")
						url = "Ses::Ses0031E.xml";
					else if (vkbur=="C600" || vkbur=="C700" || vkbur=="C800")
						url = "Ses::Ses0031S.xml";
					else
						url = "Ses::Ses0031.xml";  break;

				case "branch":
					if(F_TQUOT == true)
						url = "Ses::Ses0031.xml";
					else
						url = "Ses::Ses0051.xml";
					break;
				default: break;
			}

			gfn_openPopUpUrlHdel(url, true, arg); close();
		}
	}
}
