/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	if (length(trim(cb_month.Value)) == 0)	{
		cb_month.index = parseInt(substr(gfn_getCurrDate(),4,2))-1;
	}

	var zpym = SubStr(cb_year.Text, 0, 4) + Lpad(SubStr(cb_month.Text, 0, 2),"0",2);

	//zpym = Replace(zpym, " Y", "");
	//zpym = Replace(zpym, " M", "");

    fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.setColumn(0, "MANDT", G_MANDT);
	ds_cond.SetColumn(0, "ZPYM" , zpym   );
	ds_cond.setColumn(0, "KUNNR", ed_kunnr.Text);	

	nsfRequest(  "query"
			   , "ses0032/search"
			   , "ds_cond=ds_cond ds_list=ds_list"
			   , "ds_list=ds_list ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			  );
}

