//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_destroyWait();

	if(ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		gfn_openWSErrorWin(grid, ds_list, ds_error, "FLAG"); // error 정보 표시 
	} else {
		if(errCode != 0) gfn_showMsg("CE00001");
		else 			 gfn_showMsg("CI00002", ds_list.RowCount() + "");
	}
}

//=========================================================================================
// [ PART 6 ]
// component Event 정의 : 각 Component 별 이벤트 
//=========================================================================================
/********************************************************************************
* 기      능   : 정렬 처리 
********************************************************************************/
function grid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {

	if( nCell == 0 ) gfn_selectAllGrid(obj,nCell,nCell,true);
	else 		     gfn_sortGrid(obj, nCell);
}

/********************************************************************************
* 기      능   : 그리드 더블 클릭시 상세 화면
********************************************************************************/
function grid_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	//ds_arg.ClearData();
	//ds_arg.AddRow();
	//ds_arg.CopyRow(0, ds_list, grid.CurrentRow);

	var auart = Trim(ds_list.GetColumn(grid.CurrentRow, "AUART"));

	if ( auart == "") {
		gfn_showAlert("CW10025", "판매문서 유형이 없습니다.");
		return;
	} else {
		if (popupYn == "Y") {

			var arr = [ ds_list.GetColumn(ds_list.row, "SHANGH" ) // 국내상해구분
					  , ds_list.GetColumn(ds_list.row, "MATNR"  ) // 자재번호
					  , ds_list.GetColumn(ds_list.row, "RTYPE"  )
					  , ds_list.GetColumn(ds_list.row, "TYPE1"  )
					  , ds_list.GetColumn(ds_list.row, "TYPE2"  )
					  , ds_list.GetColumn(ds_list.row, "TYPE3"  )
					  , ds_list.GetColumn(ds_list.row, "ZNUMBER")
					  , ds_list.GetColumn(ds_list.row, "SONNR"  )]; // 수주계획번호
			close(arr);	// 배열을 연동화면으로 전송하고 팝업화면을 닫는다.
		} else {
			var arg  = "fa_sonnr=" + quote(ds_list.GetColumn(ds_list.currow, "SONNR"));
				arg += " flag="+quote("I");

			// 해외대리점 전용 화면 호출
			gfn_openPopUpUrlHdel("Ses::Ses0051E.xml", true, arg);
			close();
		}
	}
}
