/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() {

	if(Confirm("삭제하시겠습니까?") == true) {

		if ( ds_header.rowcount == 0) {
			alert("견적 Header가 없습니다.");
			return;
		} else if (flag == "I") {
			alert("신규견적등록은 삭제할 수 없습니다. 창을 닫습니다.");
			close();
		} else {
			ds_header.SetColumn(0, "FLAG", "D");

			for (var i=0; i<ds_detail.GetRowCount(); i++) {
				ds_detail.SetColumn(i, "FLAG", "U");
				ds_detail.SetColumn(i, "FLAG", "D");
			}

			nsfRequest("deleteHeader", "ses0031/saveHeader", "ds_header=ds_header", "ds_header=ds_header ds_error=ds_error", "", "fn_afterSave");
		}

	} else {
		return;
	}
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {

	var codeArr = ["ZPSTAT", "BS102", "BS403", "VKBUR", "VKGRP", "CS115"]; 	//코드콤보 설정
	var dsObjArr = [ds_zprgflg, ds_sukum, ds_zbdtyp, ds_vkbur, ds_vkgrp, ds_zregn];
	var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_SELECT]; //[G_CODE_NONE,G_CODE_NONE,G_CODE_NONE]; [G_CODE_ALL];
	var oderTypeArr = ["DTEXT1"];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	ds_header.copy(parent.ds_arg);

	flag = flag;

	if (flag == null) {
		ds_detail.copy(parent.ds_detail);
		ds_parent_detail.copy(parent.ds_detail);
	}

	// 최종 변경된 셀정보 초기화
	F_CHG_HEADER_ROWNUM = ""; // 변경된 Header 행 번호
	F_CHG_DETAIL_COLNUM = ""; // 변경된 Detail 컬럼번호
	F_CHG_DETAIL_ROWNUM = ""; // 변경된 Detail 행번호

	F_MAX_SERNO  = 0;	             // 최종순번
	F_MAX_SERNO  = fn_getMaxSerno(); // 기 저장된 최종순번

	nsfRequest("queryGtype", "ses0031/searchGtype", "ds_gtype=ds_gtype", "ds_gtype=ds_gtype ds_error=ds_error", "", "fn_afterQuery");

	div_sman.ed_sman_cd.Text = ds_header.Getcolumn(0, "ZKUNNR");

	tab.TabIndex = 0;
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

