}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_destroyWait();

	if (svcId == "queryGtype") {

		nsfRequest("querySo", "ses0031/searchSo", "ds_header=ds_header ds_so=ds_so", "ds_so=ds_so ds_error=ds_error", "", "fn_afterQuery");

	} else if (svcId == "querySo") {

		var gType   = ds_so.GetColumn(0, "GTYPE");
		var nRow    = ds_gtype.FindRow("gtype", gType);
		    zprdgbn = ds_gtype.GetColumn(nRow, "zprdgbn"); // 템플릿구분

		if (zprdgbn != null) {

			ds_cond.ClearData();
			ds_cond.AddRow();
			ds_cond.SetColumn(0, "MANDT" , ds_header.GetColumn(0, "MANDT"));
			ds_cond.SetColumn(0, "MCLASS", zprdgbn);

			nsfRequest("queryTemplate", "ses0020/find", "ds_cond=ds_cond ds_temp=ds_template", "ds_template=ds_temp ds_error=ds_error", "", "fn_afterQuery");
		}

	} else if (svcId == "queryTemplate") {

		fn_template();

		for (var i=0; i<ds_template1.GetRowCount(); i++) {
			ds_template1.SetColumn(i, "PRD", "");
		}
		for (var i=0; i<ds_template2.GetRowCount(); i++) {
			ds_template2.SetColumn(i, "PRD", "");
		}
		for (var i=0; i<ds_template3.GetRowCount(); i++) {
			ds_template3.SetColumn(i, "PRD", "");
		}
		for (var i=0; i<ds_template4.GetRowCount(); i++) {
			ds_template4.SetColumn(i, "PRD", "");
		}
		for (var i=0; i<ds_template5.GetRowCount(); i++) {
			ds_template5.SetColumn(i, "PRD", "");
		}

		if (template_flag == "") {
			if (flag == "I") btn_plus_OnClick();
		}

		template_flag = "";

	} else if (svcId == "queryHeader") {
		F_CHG_HEADER_ROWNUM = ""; // 변경된 Header 행번호
		nsfRequest("queryDetail", "ses0030/detail", "ds_cond=ds_header ds_detail=ds_detail", "ds_detail=ds_detail ds_error=ds_error", "", "fn_afterQuery");

	} else if (svcId == "queryDetail") {
		F_CHG_DETAIL_COLNUM = ""; // 변경된 Detail 컬럼번호
		F_CHG_DETAIL_ROWNUM = ""; // 변경된 Detail 행번호
	} else if (svcId == "queryCost") {

		alert("ds_t202.GetRowCount() = " + ds_t202.GetRowCount() + "\nds_t302.GetRowCount() = " + ds_t302.GetRowCount());
//		trace(ds_t202.SaveXML()); trace(ds_t302.SaveXML());
	}

	if(ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		gfn_openWSErrorWin(grd_template1, ds_template, ds_error, "FLAG"); // error 정보 표시
	} else {
		if(errCode != 0) gfn_showMsg("CE00001");
		else 			 gfn_showMsg("CI00002", ds_so.RowCount() + "");
	}
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(svcId, errCode, errMsg) {

	if(ds_error.rowcount > 0) gfn_showAlert(ds_error.GetColumn(0,"ERRMSG"));

	var qtdat   = ds_header.GetColumn(0, "QTDAT"  ); // 견적일자
	var zestdat = ds_header.GetColumn(0, "ZESTDAT"); // 입찰예정일
	var vkbur   = ds_header.GetColumn(0, "VKBUR"  ); // 사업장
	var vkgrp   = ds_header.GetColumn(0, "VKGRP"  ); // 영업그룹
	var zprgflg = ds_header.GetColumn(0, "ZPRGFLG"); // 진행상태
	var zkunnr  = ds_header.GetColumn(0, "ZKUNNR" ); // 담당자

	ds_cond.SetColumn(0, "FR_QTDAT"  , qtdat  );
	ds_cond.SetColumn(0, "TO_QTDAT"  , qtdat  );
	ds_cond.SetColumn(0, "FR_ZESTDAT", zestdat);
	ds_cond.SetColumn(0, "TO_ZESTDAT", zestdat);
	ds_cond.SetColumn(0, "VKBUR"     , vkbur  );
	ds_cond.SetColumn(0, "VKGRP"     , vkgrp  );
	ds_cond.SetColumn(0, "ZPRGFLG"   , zprgflg);
	ds_cond.SetColumn(0, "SMAN"      , zkunnr );

	switch (svcId) {
		case "saveHeader"   : nsfRequest("saveDetail"  , "ses0031/saveDetail", "ds_detail=ds_detail"                  , "ds_detail=ds_detail ds_error=ds_error", "", "fn_afterSave" ); break;
		case "saveDetail"   : var mandt = ds_header.GetColumn(0, "MANDT"); // 클라이언트
							  var qtnum = ds_header.GetColumn(0, "QTNUM"); // 견적번호
							  var qtser = ds_header.GetColumn(0, "QTSER"); // 견적차수

							  ds_cond.SetColumn(0, "MANDT", mandt);
							  ds_cond.SetColumn(0, "QTNUM", qtnum);
							  ds_cond.SetColumn(0, "QTSER", qtser);

							  nsfRequest("queryHeader" , "ses0030/header"    , "ds_cond=ds_cond   ds_header=ds_header", "ds_header=ds_header ds_error=ds_error", "", "fn_afterQuery"); break;
		case "deleteHeader"	: nsfRequest("deleteDetail", "ses0031/saveDetail", "ds_detail=ds_detail"                  , "ds_detail=ds_detail ds_error=ds_error", "", "fn_afterSave" ); break;
		case "deleteDetail" : parent.fn_query(); close();
	}
}

