/********************************************************************************
* 기      능   : 그리드 row 추가
********************************************************************************/
function btn_plus_OnClick(obj) {

	var newRow = gfn_addGridRow(grd_detail, -1, "FLAG");// 추가된 행번호 수신(FLAG:I) 

	F_MAX_SERNO++;
	ds_detail.SetColumn(newRow, "CHK" 	, 1			 );	// 선택여부
	ds_detail.SetColumn(newRow, "NEW_YN", "Y"		 );	// 신규여부
	ds_detail.SetColumn(newRow, "SERNO" , F_MAX_SERNO);	// 순번

	ds_detail.SetColumn(ds_detail.row, "MANDT"  , ds_header.getColumn(0, "MANDT"));
	ds_detail.SetColumn(ds_detail.row, "QTNUM"  , ds_header.getColumn(0, "QTNUM"));
	ds_detail.SetColumn(ds_detail.row, "QTSER"  , ds_header.getColumn(0, "QTSER"));
	ds_detail.SetColumn(ds_detail.row, "SHANGH" , ds_so.getColumn(0, "SHANGH" )); // 국내상해구분
	ds_detail.SetColumn(ds_detail.row, "MATNR"  , ds_so.getColumn(0, "MATNR"  )); // 자재번호
	ds_detail.SetColumn(ds_detail.row, "GTYPE"  , ds_so.getColumn(0, "GTYPE"  ));
	ds_detail.SetColumn(ds_detail.row, "TYPE1"  , ds_so.getColumn(0, "TYPE1"  ));
	ds_detail.SetColumn(ds_detail.row, "TYPE2"  , ds_so.getColumn(0, "TYPE2"  ));
	ds_detail.SetColumn(ds_detail.row, "TYPE3"  , ds_so.getColumn(0, "TYPE3"  ));
	ds_detail.SetColumn(ds_detail.row, "ZNUMBER", ds_so.getColumn(0, "ZNUMBER"));
}
