function ds_header_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {

	if (varNewValue != varOldValue) { 	// 값이 변경되었을 경우 변경구분 컬럼 자동 SET 
		F_CHG_HEADER_ROWNUM = nRow;	// 현재 행번호
	}
}

