function btn_cost_OnClick(obj) {

	ds_cost.ClearData();
	ds_cost.AddRow();
	ds_cost.SetColumn(0, "GBN"  , "3"); 							// 처리구분 (1: BOM, 2: 원가, 3: Web )
	ds_cost.SetColumn(0, "QTNUM", ds_header.getColumn(0, "QTNUM"));
	ds_cost.SetColumn(0, "QTSER", ds_header.getColumn(0, "QTSER"));
	ds_cost.SetColumn(0, "MATNR", ds_so.getColumn    (0, "MATNR"));
	ds_cost.SetColumn(0, "VKBUR", ds_header.getColumn(0, "VKBUR"));
	ds_cost.SetColumn(0, "VKGRP", ds_header.getColumn(0, "VKGRP"));
	ds_cost.SetColumn(0, "GJAHR", Substr(ds_header.getColumn(0, "QTDAT"), 0, 4));
	ds_cost.SetColumn(0, "POPER", "001");

	ds_char.ClearData();

	var atkla   = "";
	var prh     = "";
	var prhname = "";
	var prd     = "";

	for (var i=0; i<ds_template.GetRowCount();i++) {

		ds_char.AddRow();
		ds_char.SetColumn(i, "PSPID" , ""); // 프로젝트 정의
		ds_char.SetColumn(i, "POSID" , ""); // 작업 분석 구조 요소(WBS 요소)
		ds_char.SetColumn(i, "CD_KEY", ""); // 코드 KEY
		ds_char.SetColumn(i, "VL_KEY", ""); // 순번 KEY

		atkla   = ds_template.GetColumn(i, "ATKLA"  );
		prh     = ds_template.GetColumn(i, "PRH"    );
		prhname = ds_template.GetColumn(i, "PRHNAME");
		prd     = ds_template.GetColumn(i, "PRD"    );

		if (atkla   == null) atkla   = "";
		if (prh     == null) prh     = "";
		if (prhname == null) prhname = "";
		if (prd     == null) prd     = "";

		ds_char.SetColumn(i, "ATKLA", atkla  ); // 특성그룹
		ds_char.SetColumn(i, "ATNAM", prh    ); // 특성코드
		ds_char.SetColumn(i, "ATBEZ", prhname); // 특성이름
		ds_char.SetColumn(i, "VALUE", prd    ); // 특성값

		ds_char.SetColumn(i, "ATWTB", ""); // 특성값내역
		ds_char.SetColumn(i, "WAERS", ""); // 통화키
		ds_char.SetColumn(i, "MESSG", ""); // 메시지
	}
//	trace(ds_char.SaveXML()); trace(ds_cost.SaveXML());

	nsfRequest("queryCost", "ses0031/searchCost"
			, "ds_cost=ds_cost ds_char=ds_char ds_t202=ds_t202 ds_t302=ds_t302"
			, "ds_char=ds_char ds_t202=ds_t202 ds_t302=ds_t302 ds_error=ds_error", "", "fn_afterQuery");

    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

function Tab_OnChanging(obj,nOldIndex,nNewIndex) {

	switch (nNewIndex) {
		case "0" : btn_template.Visible = false; break;
		case "1" : if (zprdgbn != null) btn_template.Visible = true; break;
		default  : btn_template.Visible = false;
	}
}
