/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	if ( div_vbvg.cb_frvb.Value = "C600" || div_vbvg.cb_frvb.Value = "C700" || div_vbvg.cb_frvb.Value = "C800" || div_vbvg.cb_frvb.Value = "C900" ) {
	   gfn_showAlert("CW10025", "SS/주차 부서는 통합영업관리(SS/주차) 메뉴를 이용하세요");
	   return;
	}

	// 가견적 통제 프로세스 
	if ( fn_control_test_quot() == false ) {
		return;
	}

	ds_cond.DeleteAll();
	ds_cond.AddRow();
	ds_cond.setColumn(0, "MANDT"     , G_MANDT                 );
	ds_cond.SetColumn(0, "FR_QTDAT"  , cal_qtdat_fm.Value      );
	ds_cond.SetColumn(0, "TO_QTDAT"  , cal_qtdat_to.Value      );
	ds_cond.SetColumn(0, "FR_ZESTDAT", cal_soym_fm.Value       );
	ds_cond.SetColumn(0, "TO_ZESTDAT", cal_soym_to.Value       );
	ds_cond.SetColumn(0, "VKBUR"     , div_vbvg.cb_frvb.Value  );
	ds_cond.SetColumn(0, "VKGRP"     , div_vbvg.cb_frvg.Value  );
	ds_cond.SetColumn(0, "ZPRGFLG"   , cb_zpstat.Value         );
	ds_cond.SetColumn(0, "MAX_QTSER" , chk_max_qtser.Value	   ); 	//2016.12.09 최종 차수 조회 여부 추가 by mj.Shin 
	ds_cond.SetColumn(0, "QTNUM"     , ed_qtnum.Value          );
	if(G_USER_ID != "9999999") ds_cond.SetColumn(0, "SMAN"      , div_sman.ed_sman_cd.Text);

	//조회조건 제한
	var dayGap = DateTime(ds_cond.GetColumn(0, "TO_QTDAT")) - DateTime(ds_cond.GetColumn(0, "FR_QTDAT"));
	if(ds_cond.GetColumn(0, "QTNUM") == ''){
		if(ds_cond.GetColumn(0, "FR_QTDAT") == '' || ds_cond.GetColumn(0, "TO_QTDAT") == '' || dayGap > 365 )
		{
			gfn_showAlert("CW10025", "의뢰기간은 1년 이하로 설정하시기 바랍니다.");
			return;
		}
	}

	nsfRequest("header", "ses0030/header", "ds_cond=ds_cond ds_header=ds_header", "ds_header=ds_header ds_error=ds_error", "TQUOT="+F_TQUOT, "fn_afterQuery");
    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail() {

	ds_cond.setColumn(0, "MANDT", G_MANDT                                                 );
	ds_cond.SetColumn(0, "QTNUM", grd_header.GetCellText("Body", grd_header.CurrentRow, 1));
	ds_cond.SetColumn(0, "QTSER", grd_header.GetCellText("Body", grd_header.CurrentRow, 2));

	nsfRequest("detail", "ses0030/detail", "ds_cond=ds_cond ds_detail=ds_detail", "ds_detail=ds_detail ds_error=ds_error", "", "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
	if(GetTopWindow().ID == 'SD00020023') {	//가견적등록
		F_TQUOT = true;
	}
	cal_qtdat_fm.Text = gfn_getCurrDate();
	cal_qtdat_to.Text = gfn_getCurrDate();

	//var codeArr = ["SD107", "BS101", "BS102", "BS403", "CS115", "VKBUR", "VKGRP", "ZSHANG", "BS112"]; 	//코드콤보 설정
	//var dsObjArr = [ds_zpstat, ds_zprgflg, ds_sukum, ds_zbdtyp, ds_zregn, ds_vkbur, ds_vkgrp, ds_shangh, ds_ncd];
	var codeArr = ["SD107", "BS101", "BS102", "BS403", "CS115", "VKBUR", "VKGRP", "ZSHANG"]; 	//코드콤보 설정
	var dsObjArr = [ds_zpstat, ds_zprgflg, ds_sukum, ds_zbdtyp, ds_zregn, ds_vkbur, ds_vkgrp, ds_shangh];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	if ( ds_header.RowCount() > 0 )
		gfn_exportExcel(grd_header, "견적등록리스트");
	else
		gfn_showAlert("CW00010");
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	if (svcId == "header") {
	  if ( G_USER_ID == "9000593" ) {  //광장서비스 박동근  원가및 단가 조회 X  - 20140801 기술영업 유태용 CJ 요청 
		for(var i=0; i < ds_header.GetRowCount(); i++) {
		  ds_header.SetColumn(i,"ZSUM_ZCOST",0);
		  ds_header.SetColumn(i,"ZSUM_ZEAM",0);
		}
	  }	   
	  for (var j=0; j < ds_header.GetRowCount(); j++){	    
		var zrtrsn = ds_header.GetColumn(j,"ZRTRSN");
		//trace(zrtrsn);
		if (zrtrsn == "10") {
			ds_header.SetColumn(j,"ZRTRSN", "기 거래선");
		} else if (zrtrsn == "20") {
			ds_header.SetColumn(j,"ZRTRSN", "기 견적승인");
		} else if (zrtrsn == "22") {
			ds_header.SetColumn(j,"ZRTRSN", "주소불명확");
		} else if (zrtrsn == "24") {
			ds_header.SetColumn(j,"ZRTRSN", "발주처불명확");
		} else if (zrtrsn == "26") {
			ds_header.SetColumn(j,"ZRTRSN", "시행사불명확");
		} else if (zrtrsn == "30") {
			ds_header.SetColumn(j,"ZRTRSN", "수금조건");
		} else if (zrtrsn == "40") {
			ds_header.SetColumn(j,"ZRTRSN", "납기문제");
		}
	  }
		fn_detail();
	}
	else if (svcid == "detail"){

	  var qtnum = grd_header.GetCellText("Body", grd_header.CurrentRow, 1);
	  var qtser = grd_header.GetCellText("Body", grd_header.CurrentRow, 2);
	  var zkunnr = grd_header.GetCellText("Body",grd_header.CurrentRow,10);
	  var zprgflg = grd_header.GetCellText("Body",grd_header.CurrentRow,7);
	  var zrtrsn = grd_header.GetCellText("Body",grd_header.CurrentRow,49);

	  //  원가산출 단계에서는 대리점에 원가 조회 안되도록 수정 201303 김선호	  
	  if ( zprgflg = "원가산출" || zprgflg = "원가산출요청" ) {	  //주차 경우 원가산출요청일 때 
		for(var i=0; i < ds_detail.GetRowCount(); i++) {
		  ds_detail.SetColumn(i,"ZUAM",0);
		  ds_detail.SetColumn(i,"ZCOST",0);
		  }
	  }

	  if ( G_USER_ID == "9000593" ) {  //광장서비스 박동근  원가및 단가 조회 X  - 20140801 기술영업 유태용 CJ 요청 
		for(var i=0; i < ds_detail.GetRowCount(); i++) {
		  ds_detail.SetColumn(i,"ZUAM",0);
		  ds_detail.SetColumn(i,"ZCOST",0);
		  ds_detail.SetColumn(i,"ZEAM",0);
		}
	  }	  
	}  
	fn_destroyWait();

	if(ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		gfn_openWSErrorWin(grd_list, ds_header, ds_error, "FLAG");	// error 정보 표시 

	} else {
		if(errCode != 0) gfn_showMsg("CE00001");
		else             gfn_showMsg("CI00002", ds_header.RowCount() + "");
	}
} 
