********************************************************************************/
function fn_OnClick_btn(obj) {
	var fncName = "fn_" + substr(obj.id, 4); 
	eval( fncName + "()");
}

function grd_header_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	fn_detail();	
}

/********************************************************************************
* 기      능   : 그리드 더블 클릭시 상세 화면
********************************************************************************/
function grd_header_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	ds_arg.ClearData();
	ds_arg.AddRow();
	ds_arg.CopyRow(0, ds_header, grd_header.CurrentRow);

	var arg  = "fa_qtnum=" + quote(ds_header.GetColumn(nRow, "QTNUM"));
		arg += " fa_qtser=" + quote(ds_header.GetColumn(nRow, "QTSER"));
		arg += " TQUOT="+F_TQUOT;

	gfn_openPopUpUrlHdel(getDetailViewURL(), true, arg);
}

function btn_quotation_OnClick(obj) {

	// 가견적 통제 프로세스 
	if ( fn_control_test_quot() == false ) {
		return;
	}

	var arg = "branch="+quote("branch")+" zkunnr="+quote(G_USER_ID)+" zkunnrNm="+quote(G_USER_NAME)+" TQUOT="+F_TQUOT;
    gfn_openPopUpUrlHdel("Ses::Ses0032.xml", true, arg);
}

function btn_detail_OnClick(obj) {
	if (ds_header.GetRowCount() > 0	) {
		ds_arg.ClearData();
		ds_arg.AddRow();
		ds_arg.CopyRow(0, ds_header, ds_header.currow); // grd_header.CurrentRow->nRow

		var vkbur = ds_header.GetColumn(ds_header.currow, "VKBUR");
		var arg  = "fa_qtnum="  + quote(ds_header.GetColumn(ds_header.currow, "QTNUM"));
			arg += " fa_qtser=" + quote(ds_header.GetColumn(ds_header.currow, "QTSER"));
			arg += " TQUOT="+F_TQUOT;

		gfn_openPopUpUrlHdel(getDetailViewURL(), true, arg);
	} else {
		gfn_showAlert("CW10025","견적상세 대상을 선택하십시요.");
	}
}

function getDetailViewURL() {
	var url;

	if(F_TQUOT == true)
		url = "Ses::Ses0031.xml";
	else
		if ( ds_header.GetColumn(ds_header.currow, "VKBUR") == "C600" || //S/S 
			 ds_header.GetColumn(ds_header.currow, "VKBUR") == "C700" ||
			 ds_header.GetColumn(ds_header.currow, "VKBUR") == "C800" ||
			 ds_header.GetColumn(ds_header.currow, "VKBUR") == "C900" )  {
				url = "Ses::Ses0051S.xml";
		} else {
				url = "Ses::Ses0051.xml";
		}
	return url;
}

function ufn_openPjt(obj)
{
		var arg = "fa_qtnum   = "+quote(ed_qtnum.Text);		//프로젝트번호
	trace(arg);
//프로젝트명			+ " fa_zzpjt_nm  = "+quote(ed_qtso_nm.Text); 	
	var arr = gfn_openPopUpUrlHdel("Com::Com0250.xml", true, arg);			
	if (arr != null && arr != 0) {
		ed_qtnum.Text = arr[0];
	}	
}

// 가견적 통제 프로세스
function fn_control_test_quot() {

	if ( F_TQUOT == true && Left(div_sman.ed_sman_cd.Value, 1) == "A" && ( div_vbvg.cb_frvb.Value == "BC00" || div_vbvg.cb_frvb.Value == "BI00" ) ) {
	   gfn_showAlert("CW10025", "동부영업팀, 서부영업팀 소속은 가견적 기능이 통제 되었습니다.  관리팀 문의바랍니다.");
	   return false;
	} else {
		return true;
	}
}
