/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	ds_detail.ClearData();

    fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_cond.DeleteAll();
	ds_cond.AddRow();

	ds_cond.setColumn(0, "MANDT"     , G_MANDT);
	ds_cond.SetColumn(0, "FR_QTDAT"  , cal_qtdat_fm.Value);
	ds_cond.SetColumn(0, "TO_QTDAT"  , cal_qtdat_to.Value);
	ds_cond.SetColumn(0, "FR_ZESTDAT", cal_soym_fm.Value);
	ds_cond.SetColumn(0, "TO_ZESTDAT", cal_soym_to.Value);
	ds_cond.SetColumn(0, "VKBUR", 	   G_SAP_USER_VBCD);				// 
	ds_cond.SetColumn(0, "VKGRP", 	   G_SAP_USER_VGCD);				// 
	ds_cond.SetColumn(0, "ZPRGFLG",    cb_zpstat.Value);
	ds_cond.SetColumn(0, "KUNNR",      ed_kunnr.Text);			// 해외대리점코드

	ds_cond.SetColumn(0, "MAX_QTSER" , chk_max_qtser.Value	   ); 	//2016.12.09 최종 차수 조회 여부 추가 by mj.Shin 

	//조회조건 제한
	var dayGap = DateTime(ds_cond.GetColumn(0, "TO_QTDAT")) - DateTime(ds_cond.GetColumn(0, "FR_QTDAT"));
	if(ds_cond.GetColumn(0, "QTNUM") == ''){
		if(ds_cond.GetColumn(0, "FR_QTDAT") == '' || ds_cond.GetColumn(0, "TO_QTDAT") == '' || dayGap > 365 )
		{
			gfn_showAlert("CW10025", "의뢰기간은 1년 이하로 설정하시기 바랍니다.");
			return;
		}
	}

	nsfRequest(  "header"
			   , "ses0030/findAgentHeader"
			   , "ds_cond=ds_cond ds_header=ds_header"
			   , "ds_header=ds_header ds_error=ds_error"
			   , "TQUOT="+F_TQUOT
			   , "fn_afterQuery"
			  );
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail() {

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_cond.setColumn(0, "MANDT", G_MANDT                                                 );
	ds_cond.SetColumn(0, "QTNUM", grd_header.GetCellText("Body", grd_header.CurrentRow, 1));
	ds_cond.SetColumn(0, "QTSER", grd_header.GetCellText("Body", grd_header.CurrentRow, 2));

	nsfRequest(  "detail"
			   , "ses0030/findAgentDetail"
			   , "ds_cond=ds_cond ds_detail=ds_detail"
			   , "ds_detail=ds_detail ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			  );
}

