//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	var rowCnt = 0;

	fn_destroyWait();

	if(ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		// gfn_openWSErrorWin(grd_header, ds_header, ds_error, "FLAG");	// error 정보 표시 
	}
	else
	{
		if(errCode != 0)
			gfn_showMsg("CE00001");
		else
		{
			if (svcId == "header")
			{
				rowCnt = ds_header.RowCount();
				// 견적정보의 Detail 정보 조회 처리
				if (ds_header.RowCount() > 0)
				{	
					//견적상태 = 견적가승인 요청 또는 수주이후 단계에서만 견적가 확인 가능하도록 보완  - 2013.04.12 신미정 
					for ( var i=0; i< ds_header.GetRowCount(); i++) {
						var zprgflg = ds_header.GetColumn(i, "ZPRGFLG");
						if ( zprgflg != '34' && ToNumber(zprgflg) < 61 ) {
							ds_header.SetColumn(i, "ZSUM_ZEAM", 0);
						}
					}
					fn_detail();
				}
				// 헤더 정보 조회 갯수 표시
				gfn_showMsg("CI00002", rowCnt + "");
			}
			else if (svcId == "detail")
			{
				rowCnt = ds_detail.RowCount();

				//견적상태 = 견적가승인 요청 또는 수주이후 단계에서만 견적가 확인 가능하도록 보완  - 2013.04.12 신미정 
				var zprgflg = ds_header.GetColumn(ds_header.GetCurrow(), "ZPRGFLG");
				for ( var i=0; i < rowCnt; i++ ){
					if ( zprgflg != '34' && ToNumber(zprgflg) < 61 ) {
						ds_detail.SetColumn(i, "ZEAM", 0);
					}				
				}
			}
		}
	}
} 
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	ds_zpstat.Filter("CODE==" + quote("")   + " || CODE==" + quote("20") +
					 " || CODE==" + quote("27") + " || CODE==" + quote("32") +
					 " || CODE==" + quote("34") + " || CODE==" + quote("41") +
					 " || CODE==" + quote("42") + " || CODE==" + quote("61") +
					 " ||CODE==" + quote("62") + " || CODE==" + quote("90") );

	cb_zpstat.Value = "";
}

