/********************************************************************************
* 기      능   : 정렬 처리 
********************************************************************************/
function grd_header_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {

	if( nCell == 0 ) { 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		gfn_sortGrid(obj, nCell);
	}
}


function grd_header_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	if (ds_header.GetRowCount() > 0)	{
		// 수주 및 수주생성의 경우만 첨부 가능 2012.12.11
		if (ds_header.GetColumn(nRow, "ZPRGFLG") == "61" ||
			ds_header.GetColumn(nRow, "ZPRGFLG") == "90" )	{
			btn_zatt.Visible = true;
		}else	{
			btn_zatt.Visible = false;
		}

		// 파일이 첨부된 경우에만 파일 다운로드 가능
		if (length(trim(obj. GetCellValue(nRow,12))) > 0)	{
			btn_filedown.Visible = true;
		}else	{
			btn_filedown.Visible = false;
		}

		fn_detail();
	}
}

/********************************************************************************
* 기      능   : 그리드 더블 클릭시 상세 화면
********************************************************************************/
function grd_header_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

	if (ds_header.GetRowCount() > 0)
	{
		//ds_arg.ClearData();
		//ds_arg.AddRow();
		//ds_arg.CopyRow(0, ds_header, grd_header.CurrentRow);

		var arg  = "fa_qtnum=" + quote(ds_header.GetColumn(nRow, "QTNUM"));
