				(fa_atnam!="EL_AFQ" && fa_atnam!="EL_ASTQ" && fa_atnam!="ES_ARISE" && fa_atnam!="MW_ARIS" && fa_atnam!="ES_ALENG" && fa_atnam!="MW_ALENG")) ||
