/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() 
{

	var arr = ['판정일자', '12'];
	if(gfn_getDayCnt(ds_days, cal_fr_zapdat.Value, cal_to_zapdat.Value) > 365 || cal_fr_zapdat.Value == "" || cal_to_zapdat.Value == "") {
		gfn_showAlert("CW10087", arr);
		cal_fr_zapdat.SetFocus();
		return false;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_error.ClearData();

    ds_cond.DeleteAll();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "ZDEV" , ed_zdev.Value);
	ds_cond.SetColumn(0, "ZGNM" , ed_zgnm.Value);
	ds_cond.SetColumn(0, "GSNAM", ed_gsnam.Value);
	ds_cond.setColumn(0, "ADR"  , ed_adr.Value);

//--< 2017.01.17  조회조건 : 판정일자 추가 by mj.Shin Start 	
	ds_cond.setColumn(0, "FR_ZAPDAT"  , cal_fr_zapdat.Value);
	ds_cond.setColumn(0, "TO_ZAPDAT"  , cal_to_zapdat.Value);
//--> 2017.01.17  조회조건 : 판정일자 추가 by mj.Shin End 
	ds_cond.setColumn(0, "SID"  , ed_sid.Value);

	nsfRequest("query"
			 , "ses0085/find"
			 , "ds_cond=ds_cond ds_list=ds_list"
			 , "ds_list=ds_list ds_error=ds_error"
			 , ""
			 , "fn_afterQuery");
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	if (ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		//gfn_openWSErrorWin(grd_list, ds_list, ds_error, "FLAG");	// error 정보 표시 
	}else	{	
	    for(var i = 0; i < ds_list.RowCount(); i++) 
	    {   
	      if ( ds_list.GetColumn(i, "ZRTRSN") == "10" ) {
			ds_list.SetColumn(i, "ZRTRSN", "기 거래선");
	      } else if ( ds_list.GetColumn(i, "ZRTRSN") == "20" ) {
			ds_list.SetColumn(i, "ZRTRSN", "기 견적승인");
	      } else if ( ds_list.GetColumn(i, "ZRTRSN") == "22" ) {
			ds_list.SetColumn(i, "ZRTRSN", "주소불명확");
	      } else if ( ds_list.GetColumn(i, "ZRTRSN") == "24" ) {
			ds_list.SetColumn(i, "ZRTRSN", "발주처불명확");
	      } else if ( ds_list.GetColumn(i, "ZRTRSN") == "26" ) {
			ds_list.SetColumn(i, "ZRTRSN", "시행사불명확");
	      } else if ( ds_list.GetColumn(i, "ZRTRSN") == "30" ) {
			ds_list.SetColumn(i, "ZRTRSN", "수금조건");
	      } else if ( ds_list.GetColumn(i, "ZRTRSN") == "40" ) {
			ds_list.SetColumn(i, "ZRTRSN", "납기문제");
	      } else if ( ds_list.GetColumn(i, "ZRTRSN") == "90" ) {
			ds_list.SetColumn(i, "ZRTRSN", "기타");
	      } 
	    }
		gfn_showMsg("CI00002", ds_list.RowCount() + "");
		ds_list.row = FROW;		
	}

}


