// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	ds_detail.ClearData();

	ds_cond.DeleteAll();
	ds_cond.AddRow();

    fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_cond.SetColumn(0, "FR_QTDAT", cal_qtdat_fm.Value);
	ds_cond.SetColumn(0, "TO_QTDAT", cal_qtdat_to.Value);
	ds_cond.SetColumn(0, "VKBUR", 	 G_SAP_USER_VBCD);
	ds_cond.SetColumn(0, "VKGRP", 	 G_SAP_USER_VGCD);
	ds_cond.SetColumn(0, "KUNNR", 	 G_VEND_CODE);
	ds_cond.SetColumn(0, "ZAPFLG",   cb_zpstat.Value);

	nsfRequest("header"
			 , "ses0090/findAgentHeader"
			 , "ds_cond=ds_cond ds_header=ds_header ds_kunnr=ds_kunnr"
			 , "ds_header=ds_header ds_kunnr=ds_kunnr ds_error=ds_error"
			 , ""
			 , "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail() {

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_cond.SetColumn(0, "QTNUM", grd_header.GetCellText("Body", grd_header.CurrentRow, 2));
	ds_cond.SetColumn(0, "QTSER", grd_header.GetCellText("Body", grd_header.CurrentRow, 3));

	nsfRequest("detail"
			 , "ses0090/findAgentDetail"
			 , "ds_cond=ds_cond ds_detail=ds_detail ds_spec=ds_spec"
			 , "ds_detail=ds_detail ds_spec=ds_spec ds_error=ds_error"
			 , ""
			 , "fn_afterQuery");
}


/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
	// 출력 버튼 조건 충족시 표시
	btn_print_item.Visible = false;
	btn_print.Visible      = false;

	cal_qtdat_fm.Text = gfn_getCurrDate();
	cal_qtdat_to.Text = gfn_getCurrDate();

	//코드콤보 설정 
	var codeArr  = ["SD102",   "SD107",    "BS102",  "BS403",   "CS115"]; 	//코드콤보 설정
	var dsObjArr = [ds_zpstat, ds_zprgflg, ds_sukum, ds_zbdtyp, ds_zregn];

	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	// 제품군 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCodeGrid( 12, "spart", "ds_product", "grd_header");

	// 국내상해 ( componentId, comboGubun, DatasetId, 전체여부, 그리드/콤보여부 )
	fn_SetListCodeGrid( 4, "shangh", "ds_shangh", "grd_detail");

}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	var rowCnt = 0;
	fn_destroyWait();

	if(ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		gfn_openWSErrorWin(grd_header, ds_header, ds_error, "FLAG");	// error 정보 표시 
	} else {
		if(errCode != 0)
			gfn_showMsg("CE00001");
		else
		{
			if (svcId == "header")
			{
				rowCnt = ds_header.RowCount();
				// 견적정보의 Detail 정보 조회 처리
				if (ds_header.RowCount() > 0)
				{
					grd_header_OnCellClick();
					//fn_detail();
				}
			}
			else if (svcId == "detail")
			{
				rowCnt = ds_detail.RowCount();
			}

			gfn_showMsg("CI00002", rowCnt + "");
		}
	}
} 
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_zpstat.index = 0;
	ds_zpstat.Filter("CODE !="+quote("20"));
}

