/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_Header_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if ( nCell == 0 ) {
		return;
	}
	else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_header_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_header.fireevent = false;
	if(strColumnID!="CHECK"){
		ds_header.SetColumn(nRow, "CHECK", 1);
	}
	ds_header.fireevent = true;
}

/********************************************************************************
* 기      능   : 견적승인
********************************************************************************/
function btn_approval_OnClick(obj)
{
	// 그리드 저장 대상 로우
	if(ds_header.FindRow("CHECK", 1) == -1){
		gfn_showAlert('CW00046');
		return;
	}

	ds_header2.ClearData();

	ds_header.Filter("CHECK==1");
	ds_header2.copyF(ds_header);
	ds_header.unFilter();

	// 승인상태가 접수인건만 대상으로 처리
	for (var i = 0; i < ds_header2.RowCount(); i++)		{
		if (ds_header2.GetColumn(i, "ZAPFLG") != "10")	{
			gfn_showAlert("CW10025", "Only the quotation with pending confirmation request can be processed.");
			return false;
		}
	}

	if ( !gfn_showConfirm("CW10025", "Would you like to process the confirmation of the Quotation?") )	return false;

	tit_clearActionInfo();

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	// 서비스 호출 
	ds_error.ClearData(); 

	nsfRequest("approval"																	// srvId
				, "ses0090/agentApproval"															// url
				, "ds_list=ds_header2"														// inDsList
				, "ds_error=ds_error"												    	// outDsList
				, ""																		// strParam
				, "fn_afterApproval"															// CallBackFunc
				);

}

/********************************************************************************
* 기      능   : 승인 Callback  
********************************************************************************/
function fn_afterApproval(errCode, errMsg) {
	fn_destroyWait();

	ds_header2.ClearData(); 

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");		
	}

	fn_query();
}

/********************************************************************************
* 기      능   : 견적반려
********************************************************************************/
function btn_reject_OnClick(obj)
{
	// 그리드 저장 대상 로우
	if(ds_header.FindRow("CHECK", 1) == -1){
		gfn_showAlert('CW00046');
		return;
	}

	ds_header2.ClearData();

	ds_header.Filter("CHECK==1");
	ds_header2.copyF(ds_header);
	ds_header.unFilter();

	// 승인상태가 접수인건만 대상으로 처리
	for (var i = 0; i < ds_header2.RowCount(); i++)		{
		if (ds_header2.GetColumn(i, "ZAPFLG") != "10")	{
			gfn_showAlert("CW10025", "Only the quotation with pending confirmation request can be processed.");
			return false;
		}

		if ( trim(ds_header2.GetColumn(i, "ZRTRSN")) == '' || trim(ds_header2.GetColumn(i, "ZRTRSN")) == null )	{
			gfn_showAlert("CW10025",     "Please enter the reason for the rejection of Quot # [" + ds_header2.GetColumn(i, "QTNUM") + "]");
			//gfn_SetFocusGrid(grd_header, grd_header.CurrentRow, 9);
			return false;
		}
	}

	if ( !gfn_showConfirm("CW10025", "Would you like to reject the quotation confirmation request?") )	return false;

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	tit_clearActionInfo();

	// 서비스 호출 
	ds_error.ClearData(); 

	nsfRequest("reject"																	// srvId
				, "ses0090/agentReject"													// url
				, "ds_list=ds_header2"														// inDsList
				, "ds_error=ds_error"												    	// outDsList
				, ""																		// strParam
				, "fn_afterReject"													// CallBackFunc
				);

}

/********************************************************************************
* 기      능   : 반려 Callback  
********************************************************************************/
function fn_afterReject(errCode, errMsg) {
	fn_destroyWait();

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00005");		
	}

	fn_query();
}


function grd_header_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	// 출력버튼 표시
	if(ds_header.GetRowCount() > 0)	{
		if (ds_header.GetColumn(nRow, "ZAPFLG") == "30")	{	// 승인시에만 버튼 표시
			btn_print_item.Visible = true;
			btn_print.Visible      = true;
		}else	{
			btn_print_item.Visible = false;
			btn_print.Visible      = false;			
		}

		fn_detail();
	}
}


/********************************************************************************
* 기      능   : Grid Double click 시 견적상세 정보 조회
********************************************************************************/
function grd_header_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_header.GetRowCount() > 0)
	{
	//ds_arg.ClearData();
	//ds_arg.AddRow();
	//ds_arg.CopyRow(0, ds_header, grd_header.CurrentRow);
		var arg  = "fa_qtnum="  + quote(ds_header.GetColumn(ds_header.currow, "QTNUM"));
			arg += " fa_qtser=" + quote(ds_header.GetColumn(ds_header.currow, "QTSER"));

		gfn_openPopUpUrlHdel("Ses::Ses0051E.xml", true, arg);
	}
}


/********************************************************************************
* 기      능   : 출력 (계약서)
