function btn_print_OnClick(obj)
{
	if (ds_header.GetRowCount() > 0)
	{
		fn_afterPrint("contract");	// 계약서 출력
	}
}

/********************************************************************************
* 기      능   : 출력 (사양서)
********************************************************************************/
function btn_print_item_OnClick(obj)
{
	if (ds_header.GetRowCount() > 0)
	{
		fn_afterPrint("item");	// 사양서 출력
	}
}

/********************************************************************************
* 기      능   :  출력 처리
********************************************************************************/
function fn_afterPrint(strFlag) {

	if (ds_kunnr.GetRowCount() <= 0)
	{
		gfn_showMsg("CE00001");
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	var kunnr_tel  = ds_kunnr.GetColumn(0, "TELF1");	// 전화번호
	var kunnr_fax  = ds_kunnr.GetColumn(0, "TELFX");	// FAX번호
	var messers    = "HYUNDAI ELEVATOR CO., LTD.";	// MESSERS
	var qtnum      = ds_header.GetColumn(ds_header.currow, "QTNUM");	// 견적번호
	var qtser      = ds_header.GetColumn(ds_header.currow, "QTSER");	// 견적차수

	var pro_dev     = ds_header.GetColumn(ds_header.currow, "ZDEV");	// 시공사
	var constructor = "";	// 확인 후 필요 시 조회 필요
	var org_of_prd  = "";	// 확인 후 필요 시 조회 필요
	if (ds_detail.GetColumn(0, "SHANGH") == "상해")		{
		org_of_prd = "CHINA";
	}else if (ds_detail.GetColumn(0, "SHANGH") == "국내")	{
		org_of_prd = "KOREA";
	}

	var ship_by     = "";	// 확인 후 필요 시 조회 필요
	var ship_to     = "";	// 확인 후 필요 시 조회 필요
	var term_of_trd = ds_header.GetColumn(ds_header.currow, "INCO");	// 인도조건
	var delivery_dt = ds_header.GetColumn(ds_header.currow, "DELDAT");	// 납품예정일
	    delivery_dt = substr(delivery_dt, 0, 4) + "." + substr(delivery_dt, 4, 2) + "." + substr(delivery_dt, 6, 2);
	var term_of_pay = "";	// 확인 후 필요 시 조회 필요
	var waerk       = ds_header.GetColumn(ds_header.currow, "WAERK"); 		//화폐단위
	var tot_amount  = ds_header.GetColumn(ds_header.currow, "ZSUM_ZEAM");	// 합계 견적가
	var st_of_const = "";	// 확인 후 필요 시 조회 필요
	var proj_name   = ds_header.GetColumn(ds_header.currow, "GSNAM"); 	//공사명
	var to_date     = substr(gfn_getCurrDate(), 0, 4) + "." + substr(gfn_getCurrDate(), 4, 2) + "." + substr(gfn_getCurrDate(), 6, 2);

	// 헤더 정보를 ds_pheader에 담아서 처리
	ds_pheader.ClearData();
	ds_pheader.AddRow();

	ds_pheader.SetColumn(0, "KUNNR_NM",  ed_kunnr_nm.Text);		// 고객명
	ds_pheader.SetColumn(0, "TELF1",     kunnr_tel);			// TEL
	ds_pheader.SetColumn(0, "TELFX",     kunnr_fax);			// FAX
	ds_pheader.SetColumn(0, "TO_DATE",   to_date);	// today
	ds_pheader.SetColumn(0, "MESSERS",   messers);				// messers
	ds_pheader.SetColumn(0, "ATTENTION", G_SAP_USER_PMNM_B);	// ATTENTION (본팀의 대리점 담당자)
	ds_pheader.SetColumn(0, "QTNUM",     qtnum);				// QTNUM 견적번호
	ds_pheader.SetColumn(0, "QTSER",     qtser);				// QTSER 견적차수
	ds_pheader.SetColumn(0, "PRO_DEV",     pro_dev);				// PROJECT OF DEVELOPER
	ds_pheader.SetColumn(0, "CONSTRUCTOR", constructor);		// CONSTRUCTOR
	ds_pheader.SetColumn(0, "ORG_OF_PRD",  org_of_prd);			// ORIGIN OF PRODUCT
	ds_pheader.SetColumn(0, "SHIP_BY",     ship_by);			// SHIP BY
	ds_pheader.SetColumn(0, "SHIP_TO",     ship_to);			// SHIP TO
	ds_pheader.SetColumn(0, "TERM_OF_TRD", term_of_trd);		// TERM OF TRADE
	ds_pheader.SetColumn(0, "DELIVERY_DT", delivery_dt);		// DELIVERY DATE
	ds_pheader.SetColumn(0, "TERM_OF_PAY", term_of_pay);		// TERM OF PAYMENT
	ds_pheader.SetColumn(0, "WAERK",       waerk);				// TERM OF PAYMENT
	ds_pheader.SetColumn(0, "TOT_AMOUNT",  NumFormat(toNumber(tot_amount), 2));	// TOTAL AMOUNT
	ds_pheader.SetColumn(0, "ST_OF_CONST", st_of_const);		// STAGE OF CONSTRUCTION WORK
	ds_pheader.SetColumn(0, "PROJ_NAME",   proj_name);		// PROJ_NAME
	ds_pheader.SetColumn(0, "DATETIME",    to_date);
	ds_pheader.SetColumn(0, "NOTE",        fn_setWrite(ds_kunnr.GetColumn(0, "LAND1") ));

	var strUnit = "UNIT";
	ds_pdetail.ClearData();		// 계약서 SPEC 및 SPEC출력에서 사용
	ds_pdetail_h.ClearData();	// 계약서 헤더에서 사용

	var strDesc = "";

	// 리스트 정보는 ds_pdetail정보를 copy하여 사용함
	for (var i = 0; i < ds_detail.GetRowCount(); i++)
	{

		ds_pdetail.AddRow();
		ds_pdetail.SetColumn(i, "QTNUM",       ds_detail.GetColumn(i, "QTNUM"));
		ds_pdetail.SetColumn(i, "QTSER",       ds_detail.GetColumn(i, "QTSER"));
		ds_pdetail.SetColumn(i, "QTSEQ",       ds_detail.GetColumn(i, "QTSEQ"));
		ds_pdetail.SetColumn(i, "MODEL",       ds_detail.GetColumn(i, "SPEC"));
		ds_pdetail.SetColumn(i, "DELIVERY_DT", delivery_dt);
		ds_pdetail.SetColumn(i, "COOP_NAME",   ed_kunnr_nm.Text);
		ds_pdetail.SetColumn(i, "SALE_PER",    G_SAP_USER_PMNM_B);
		ds_pdetail.SetColumn(i, "TEL_NO",      kunnr_tel);
		ds_pdetail.SetColumn(i, "TECH_PER",    "");

		ds_pdetail.SetColumn(i, "PROJ_NAME", proj_name);
		strDesc = Replace(ds_detail.GetColumn(i, "SPEC"), ds_detail.GetColumn(i, "GTYPE") + " " , "");
		ds_pdetail.SetColumn(i, "DESC",      strDesc);
		ds_pdetail.SetColumn(i, "OPER",      ds_detail.GetColumn(i, "OPER"));
		ds_pdetail.SetColumn(i, "QTY",       NumFormat(toNumber(ds_detail.GetColumn(i, "ZNUMBER")), 0));

		if (toNumber(ds_detail.GetColumn(i, "ZNUMBER")) > 1)
			strUnit = "UNITS";

		ds_pdetail.SetColumn(i, "UNIT",      strUnit);
		ds_pdetail.SetColumn(i, "U_PRICE",   toNumber(ds_detail.GetColumn(i, "ZEAM")) / toNumber(ds_detail.GetColumn(i, "ZNUMBER")));
		ds_pdetail.SetColumn(i, "WAERK",     waerk);
		ds_pdetail.SetColumn(i, "AMOUNT",    ds_detail.GetColumn(i, "ZEAM"));

		// SPEC 출력을 위한 처리

		// 해당 견적일련번호로 필터 처리
		//ds_spec.Filter("QTSER==" + quote(ds_detail.GetColumn(i, "QTSER")));

		// _이후의 명칭으로 표시
		for (var ii = 0; ii < ds_spec.GetRowCount(); ii++)		{
			ds_spec.SetColumn(ii, "GBN",  substr(ds_spec.GetColumn(ii, "ATKLA"), pos(ds_spec.GetColumn(ii, "ATKLA"), "_") + 1));
		}
	}

	fn_destroyWait();

	if   (strFlag == "contract")	{
		Dialog("SES::Ses0091E.xml", "", -1, -1, "Resize=true");	// UbiReport Call
	}else if (strFlag == "item")	{
		Dialog("SES::Ses0092E.xml", "", -1, -1, "Resize=true");	// UbiReport Call
	}
}

/********************************************************************************
* 기      능   :  출력의 국가별 문구 작성
********************************************************************************/
function fn_setWrite(strLand1)
{
	var rtnNote;
	var strNoteE = "";
	strNoteE += "      - Signing of this Purchase Order admits the Buyer and Seller's to the Standard Terms of Sales. \n";
	strNoteE += "\n";
	strNoteE += "      - The Buyer assumes the responsibility for the actual costs incurred by the Seller in case of \n";
    strNoteE += "        cancellation of the Purchase Order. \n";
    strNoteE += "\n";
    strNoteE += "      - The Seller reserves the right to escalate the contract amount if the material costs \n";
    strNoteE += "        (rare earth metals, steel products, etc.) increases by more then 10% of the Seller's costs. \n";
    strNoteE += "\n";
    strNoteE += "      - The Seller reserves the right to escalate the contract amount in consideration of the \n";
    strNoteE += "        fluctuationof the raw material costs, currency exchange rate, and/or inflation if the \n";
    strNoteE += "        delivery date is delayed for more then 3 months or have exceeded the the contractual calendar \n";
    strNoteE += "        year. If the delivery date is delayed for more than 6 months, the Seller reserves the \n";
    strNoteE += "        right to cancel the Purchase Order. \n";
    strNoteE += "\n";
    strNoteE += "      - Attachement of the detialed specification is required for this Purchase Order to be vaild.";

	var strNoteJ = "";
	strNoteJ += "     JJ - Signing of this Purchase Order admits the Buyer and Seller's to the Standard Terms of Sales. \n";
	strNoteJ += "\n";
	strNoteJ += "      - The Buyer assumes the responsibility for the actual costs incurred by the Seller in case of \n";
    strNoteJ += "        cancellation of the Purchase Order. \n";
    strNoteJ += "\n";
    strNoteJ += "      - The Seller reserves the right to escalate the contract amount if the material costs \n";
    strNoteJ += "        (rare earth metals, steel products, etc.) increases by more then 10% of the Seller's costs. \n";
    strNoteJ += "\n";
    strNoteJ += "      - The Seller reserves the right to escalate the contract amount in consideration of the \n";
    strNoteJ += "        fluctuationof the raw material costs, currency exchange rate, and/or inflation if the \n";
    strNoteJ += "        delivery date is delayed for more then 3 months or have exceeded the the contractual calendar \n";
    strNoteJ += "        year. If the delivery date is delayed for more than 6 months, the Seller reserves the \n";
    strNoteJ += "        right to cancel the Purchase Order. \n";
    strNoteJ += "\n";
    strNoteJ += "      - Attachement of the detialed specification is required for this Purchase Order to be vaild.";

	var strNoteK = "";
	strNoteK += "     KK - Signing of this Purchase Order admits the Buyer and Seller's to the Standard Terms of Sales. \n";
	strNoteK += "\n";
	strNoteK += "      - The Buyer assumes the responsibility for the actual costs incurred by the Seller in case of \n";
    strNoteK += "        cancellation of the Purchase Order. \n";
    strNoteK += "\n";
    strNoteK += "      - The Seller reserves the right to escalate the contract amount if the material costs \n";
    strNoteK += "        (rare earth metals, steel products, etc.) increases by more then 10% of the Seller's costs. \n";
    strNoteK += "\n";
    strNoteK += "      - The Seller reserves the right to escalate the contract amount in consideration of the \n";
    strNoteK += "        fluctuationof the raw material costs, currency exchange rate, and/or inflation if the \n";
    strNoteK += "        delivery date is delayed for more then 3 months or have exceeded the the contractual calendar \n";
    strNoteK += "        year. If the delivery date is delayed for more than 6 months, the Seller reserves the \n";
    strNoteK += "        right to cancel the Purchase Order. \n";
    strNoteK += "\n";
    strNoteK += "      - Attachement of the detialed specification is required for this Purchase Order to be vaild.";

	switch(strLand1)	{
		case "KU" :
			rtnNote = strNoteK;
			break;
		case "JO" :
			rtnNote = strNoteJ;
			break;
		default :
			rtnNote = strNoteE;
		    break;
	}
	return rtnNote;
}

/********************************************************************************
* 기      능   :  SPEC 정보 추출
********************************************************************************/
function fn_specInfo(nRow, strMatnr, strRtype, strType1, strType2, strType3, strZacapa, strZuse, strZopn, strZlen)
{
	var strVkbur = ds_header.GetColumn(ds_header.currow, "VKBUR");
	var strSpec  = "";
	strSpec = gfn_specInfo(strVkbur, strMatnr, strRtype, strType1, strType2, strType3, strZacapa, strZuse, strZopn, strZlen);
	ds_detail.SetColumn(nRow, "SPEC", strSpec);
	return strSpec;
}
