/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_error.ClearData();
	ds_cond.DeleteAll();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "FR_QTDAT"  , cal_qtdat_fm.Value      );
	ds_cond.SetColumn(0, "TO_QTDAT"  , cal_qtdat_to.Value      );
	ds_cond.SetColumn(0, "FR_ZESTDAT", cal_soym_fm.Value       );
	ds_cond.SetColumn(0, "TO_ZESTDAT", cal_soym_to.Value       );
	ds_cond.SetColumn(0, "VKBUR"     , div_vbvg.cb_frvb.Value  );
	ds_cond.SetColumn(0, "VKGRP"     , div_vbvg.cb_frvg.Value  );
	ds_cond.SetColumn(0, "ZPRGFLG"   , cb_zpstat.Value         );
	ds_cond.SetColumn(0, "SMAN"      , div_sman.ed_sman_cd.Text);
	ds_cond.SetColumn(0, "INCO"      , cb_inco1.Value);

	nsfRequest(  "header"
			   , "ses0150/header"
			   , "ds_cond=ds_cond ds_header=ds_header"
			   , "ds_header=ds_header ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			  );
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail() {

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_cond.SetColumn(0, "QTNUM", grd_header.GetCellText("Body", grd_header.CurrentRow, 1));
	ds_cond.SetColumn(0, "QTSER", grd_header.GetCellText("Body", grd_header.CurrentRow, 2));

	nsfRequest("detail", "ses0150/detail", "ds_cond=ds_cond ds_detail=ds_detail", "ds_detail=ds_detail ds_error=ds_error", "", "fn_afterQuery");
}


/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
	//cal_qtdat_fm.Text = gfn_getCurrDate();
	//cal_qtdat_to.Text = gfn_getCurrDate();
	//cal_soym_fm.Text  = gfn_getCurrDate();
	//cal_soym_to.Text  = gfn_getCurrDate();

	//코드콤보 설정 
	var codeArr    = ["ZPSTAT",  "BS101",    "BS102",  "BS403",   "CS115", "VKBUR",   "VKGRP",  "ZSHANG"]; 	//코드콤보 설정
	var dsObjArr   = [ds_zpstat, ds_zprgflg, ds_sukum, ds_zbdtyp, ds_zregn, ds_vkbur, ds_vkgrp, ds_shangh];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	div_vbvg.st_vb_bar.Visible = false;
	div_vbvg.st_vg_bar.Visible = false;
	div_vbvg.cb_tovb.Visible   = false;
	div_vbvg.cb_tovg.Visible   = false;

	div_vbvg.cb_frvg.Top       = 24;

	fn_query_zterm();   // 지급조건   
	fn_query_inco(); 	// 인도조건   	
}

