function fn_OnClick_btn(obj) {
	var fncName = "fn_" + substr(obj.id, 4); 
	eval( fncName + "()");
}

function grd_header_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	fn_detail();
}



/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterReceipt(errCode, errMsg) {
	fn_destroyWait();

	ds_header2.ClearData(); 

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}

	fn_query();
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterReceiptCancel(errCode, errMsg) {
	fn_destroyWait();

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}

	fn_query();
}

function fn_afterReceiptCancel(errCode, errMsg) {
	fn_destroyWait();

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}

	fn_query();
}

/********************************************************************************
* 기      능   : 기능검토결과등록 팝업
********************************************************************************/
function btn_result_OnClick(obj)
{

	if(ds_header.rowCount == 0) {

		//alert("데이터를 선택해주십시오."); 
		// [${}] 자료가 존재하지 않습니다.
		gfn_showAlert("CW10021", "저장");

		return; 
	}	

	// if(ds_header.getColumn(ds_header.row, "ZRQSTAT") == "03") {
		// alert("이미 기술 검토의뢰 되었습니다."); 
		// return false; 					
	// }		


	if(ds_header.getColumn(ds_header.row, "ZRQSTAT") == "05") {
		alert("이미 기술 검토완료 처리 되었습니다."); 
		return false; 					
	}			


	ds_arg.ClearData();
	ds_arg.AddRow();
	ds_arg.CopyRow(0, ds_header, ds_header.row);

	var arg = "flag="+quote("U");
	gfn_openPopUpUrlHdel("Ses::Ses0151.xml", true, arg);

	fn_query();

}

// 지급조건 코드목록 조회함수
function fn_query_zterm() 
{     
	nsfRequest("query"												// srvId
				, "combo/comboZterm"								// url
				, "ds_list_zterm=ds_list_zterm"						// inDsList
				, "ds_list_zterm=ds_list_zterm ds_error=ds_error"	// outDsList
				, ""												// strParam
				, "fn_afterQuery_zterm"								// CallBackFunc
				); 
}   

// 지급조건 코드목록 조회 CALL BACK함수
function fn_afterQuery_zterm(errCode, errMsg) 
{    
	// HEAD의 지급조건은 현금(0001), 어음(0002) 만 보이도록 Filtering
	gfn_copyDs(ds_list_zterm, ds_list_zterm_head, "CODE=='0001' || CODE=='0002'");

	gfn_InsertRowCombo(ds_list_zterm	 , 0, "N", "Y", "CODE", "CODE_NAME"); // 선택 행 추가 
	gfn_InsertRowCombo(ds_list_zterm_head, 0, "N", "Y", "CODE", "CODE_NAME"); // 선택 행 추가 
	cb_zterm.index = 0;
}   

// 인도조건 코드목록 조회함수
function fn_query_inco() 
{     
	nsfRequest("query"												// srvId
				, "combo/comboInco"									// url
				, "ds_list_inco1=ds_list_inco1"						// inDsList
				, "ds_list_inco1=ds_list_inco1 ds_error=ds_error"	// outDsList
				, ""												// strParam
				, "fn_afterQuery_inco"								// CallBackFunc
				); 
}   

// 인도조건 코드목록 조회 CALL BACK함수
function fn_afterQuery_inco(errCode, errMsg) 
{    
	gfn_InsertRowCombo(ds_list_inco1, 0, "N", "Y", "CODE", "CODE_NAME"); // 선택 행 추가
	cb_inco1.index = 0;
}   



function ds_header_OnRowPosChanged(obj,nOldRow,nRow)
{
	fn_detail();
}
