function fn_OnLoadCompleted(obj) {

//	gfn_initForm();		// Form 로드 이벤트 맨 첫줄에 기술 
	div_btn.fn_loadUseBtn("30", "견적원가등록팝업", "", "");   	// 화면제목 설정 및 버튼 활성화

	F_MANDT = fa_mandt;  
	F_QTNUM = fa_qtnum;
	F_QTSER = fa_qtser;
	F_QTSEQ = fa_qtseq;

	http.sync = true;		

	if ( isExistVar("fa_part", true) ) {
		F_PART = trim(fa_part);
		grd_list.SetColProp(20,"Width",80);
		grd_list.SetColProp(21,"Width",0);
		grd_list_detail.SetColProp(10,"Width",80);
		grd_list_detail.SetColProp(15,"Width",0);
		grd_list_detail.SetColProp(16,"Width",0);

		fn_set_field_visible(false);

	}else	{ //중계무역 
		F_PART = "";	
		grd_list.SetColProp(20,"Width",0);
		grd_list.SetColProp(21,"Width",80);
		grd_list_detail.SetColProp(10,"Width",0);
		grd_list_detail.SetColProp(15,"Width",80);		
		grd_list_detail.SetColProp(16,"Width",80);		

		fn_set_field_visible(true);
		fn_get_exchange_rate();		
	}

	fn_query();

	http.sync = false;
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() { 

	gfn_clearMsgBar();
	gfn_clearGridSortMark(grd_list); 	// sort mark 삭제 

//	if (fn_valid_chk("query") == false)  return; // 조회조건 항목오류검증, 주석처리 2018.04.16 by mj.Shin 
	if (fn_param_set("query") == false)  return; // 조회조건 설정

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_list.ClearData();  	// 그리드 데이타 초기화

	nsfRequest(  "find"
			   , "ses0171/find"
			   , "ds_cond=ds_cond ds_list=ds_list"
			   , "ds_list=ds_list ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			   ); // 서비스호출
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) { 

	fn_destroyWait();

	//상해단가추가 - 2014.05.21 신미정 수정 - 정동명CJ 요청
	if (F_PART != "part")	{
	    st_zurate.text = " * 상해단가 간접비율 : " + ds_list.GetColumn(0, "ZURATE");	
	}

	if(ds_error.GetRowCount() > 0) {  
		// 환율정보가 없습니다.
		if (ds_error.GetColumn(0, "ERRMSG") == "CE10007")
		{
			gfn_showAlert(ds_error.GetColumn(0, "ERRMSG"));
			gfn_showMsgBar(ds_error.GetColumn(0,"ERRMSG"), ds_error.GetColumn(0,"ERRCODE"));
			return false;
		} else {		
			gfn_showMsg("CE00001");
		//gfn_openWSErrorWin(grd_list, ds_list, ds_error, ""); // error 정보 표시 
		}
	} else 
		F_QUERY_FLAG = "Q";	//fn_query_detail();
	}
}

function fn_param_set(pProcGb) { // 호출 파라메터 SET

	ds_cond.ClearData();
	ds_cond.AddRow();
	ds_cond.SetColumn(0, "MANDT", F_MANDT);
	ds_cond.SetColumn(0, "QTNUM", F_QTNUM);
	ds_cond.SetColumn(0, "QTSER", F_QTSER);
	ds_cond.SetColumn(0, "QTSEQ", F_QTSEQ);
	ds_cond.SetColumn(0, "KURST", "Q");
	ds_cond.ApplyChange();
	return true;
}   

function fn_valid_chk(pProcGb) {// 항목 오류 검증 // pProcGb : 처리구분 (query:조회)

	switch(pProcGb) {
		case "excel" : 
			if (ds_list.RowCount() <=0) 
				gfn_showAlert("CW00113", "엑셀 다운로드"); 
			break; // 엑셀
		case "query" : 
			if ( F_PART = "" && ParseCurrency(ed_ukurs.Text) =0 ) {
				btn_add.Enable = False;
				btn_del.Enable = False;
				return false;
			} else {
				btn_add.Enable = True;
				btn_del.Enable = True;
			}
		default: break;
	}
	return true;
}

/********************************************************************************
* 기      능   : 조회 (detail 조회)
********************************************************************************/
function fn_query_detail() { 

	if (ds_list.rowcount() < 1) 
		return;

	gfn_clearMsgBar();
	gfn_clearGridSortMark(grd_list_detail); 	// sort mark 삭제 

	if (fn_valid_chk("query") == false)  return; // 조회조건 항목오류검증

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	var nRow = ds_list.GetRowPos();

	ds_cond_detail.ClearData();
	ds_cond_detail.AddRow();
	ds_cond_detail.setColumn(0, "MANDT", ds_list.GetColumn(nRow, "MANDT"));
	ds_cond_detail.setColumn(0, "QTNUM", ds_list.GetColumn(nRow, "QTNUM"));
	ds_cond_detail.setColumn(0, "QTSER", ds_list.GetColumn(nRow, "QTSER"));
	ds_cond_detail.setColumn(0, "QTSEQ", ds_list.GetColumn(nRow, "QTSEQ"));
	ds_cond_detail.setColumn(0, "FCURR", ed_fcurr.Text);
	var ukurs = "";
	if(F_PART == "part") {	// 설계부(PART) , 해외영업 및 상해("") 구분값
		ukurs = "1";							// 설계부 - 부부원가 입력 단위 : KRW , 환율 = 1
	} else {
		ukurs = ParseCurrency(ed_ukurs.Text);	// 중계무역- 부부원가 입력 단위 : COST TABLE 배포 통화, 계약 통화 환율 
	}
	ds_cond_detail.setColumn(0, "UKURS", ukurs);
	ds_cond.ApplyChange();

	ds_detail.ClearData();  	// 그리드 데이타 초기화

	// 서비스호출
	nsfRequest("query_list_detail"									// srvId
				, "ses0171/query_list_detail"						// url
				, "ds_cond_detail=ds_cond_detail ds_detail=ds_detail"		// inDsList
				, "ds_detail=ds_detail ds_error=ds_error"	// outDsList
				, ""									// strParam
				, "fn_afterQuery_detail"				// CallBackFunc
				);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery_detail(errCode, errMsg) { 

	fn_destroyWait();

	if(ds_error.rowcount() > 0) {  
		gfn_showMsg("CE00001");
		gfn_openWSErrorWin(ds_detail, ds_detail, ds_error, "");		// error 정보 표시 
	} else {
		for(var i = 0; i < ds_detail.rowcount(); i++ ) { 
			ds_detail.SetColumn(i, "FLAG", "");
		}

		if (ds_detail.GetRowCount() > 0)
		{	
			if (F_PART != "part")	{
				ed_sum.Text = ds_detail.Sum("ZUAMS");		
			} else {
				ed_sum.Text = ds_detail.Sum("ZUAM");		
			}
		} 		

		// 견적진행상태에 따른 화면 처리
		var strZprgflg = ds_list.GetColumn(ds_list.currow, "ZPRGFLG");

		if (F_PART != "part")	{
			// 해외본팀에서 호출된 경우이며 견적진행상태가 
			// 견적작성(20), 견적요청(27) -> 등록/ 수정가능, 원가산출완료(32) -> 등록/ 수정만 가능
			if(strZprgflg == "20" || strZprgflg == "27")	{
				btn_add.Visible          = true;
				btn_del.Visible          = true;
				grd_list_detail.Editable = true;
			}
			else if (strZprgflg == "32")	{
				btn_add.Visible          = true;
				btn_del.Visible          = true;
				grd_list_detail.Editable = true;
			}else	{
				btn_add.Visible          = false;
				btn_del.Visible          = false;
				grd_list_detail.Editable = false;
			}
		}else	{
			// 설계부에서 처리 시 원가팀의뢰(41)의 경우 등록/수정
			// 원가팀완료(42)의 경우 등록/수정
			if (strZprgflg == "41")	{
				btn_add.Visible          = true;
				btn_del.Visible          = true;
				grd_list_detail.Editable = true;
			}else if (strZprgflg == "42")	{
				btn_add.Visible          = true;
				btn_del.Visible          = true;
				grd_list_detail.Editable = true;
			}else	{
				btn_add.Visible          = false;
				btn_del.Visible          = false;
				grd_list_detail.Editable = false;
			}
		}
	}
}

