/********************************************************************************
* 기      능   : 저장 버튼 클릭 시
********************************************************************************/
function fn_save() {
	var cntDetail = 0;

	// 변견정보 존재 유무 확인 후 처리
	destroy("ds_temp");
	create("dataset", "ds_temp");
	ds_temp.copy(ds_detail);
	ds_temp.Filter("FLAG != null && FLAG != ''");
	cntDetail = ds_temp.GetRowCount();
	ds_temp.UnFilter();

/*  Data set을 구성해서 변경 여부 CHECK (Dataset의 OnColumnChanged 이벤트 처리 오류 개선) 
	ds_detail.Filter("FLAG != null && FLAG != ''");
	cntDetail = ds_detail.GetRowCount();
	ds_detail.UnFilter();
*/

	if (cntDetail <= 0)	{
		gfn_showAlert("CW00005");
		return;
	}

	if(!gfn_showConfirm("CI00008"))		{
	   return;
	}

	fn_detail_save();
}

/********************************************************************************
* 기      능   : 저장 버튼 클릭 유효성 확인
********************************************************************************/
function fn_detail_save() {
	var zuam = 0;	
	var zuams = 0;
	var zurate = 0;	
	var zurate2 = 0;
	var strFlag = "";
	var strVkbur = "";

	gfn_clearGridSortMark(grd_list_detail);

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	// 추가 항목의 ZCOSTZSEQ에 value지정.
	for (var i = 0; i < ds_detail.GetRowCount(); i++)	{
		strFlag = ds_detail.getColumn(i, "FLAG");
		if (strFlag == "I")	{
			ds_detail.SetColumn(i, "ZCOSTZSEQ", i + 1);
			ds_detail.SetColumn(i, "ZURATE", ds_list.GetColumn(0, "ZURATE"));
		}

		//간접비율 포함된 단가 재계산(ZUAM)
		if (strFlag == "I" || strFlag == "U") {
			strVkbur = ds_list.GetColumn(ds_list.currow, "VKBUR");
			if ( (strVkbur == "B500" || strVkbur == "B600" || strVkbur = 'B700' || strVkbur == "B900" || strVkbur = 'BA00' || strVkbur = 'BB00' ) AND F_PART != "part" ) {	// 해외의 경우
				zuams = ParseFloat(ds_detail.Getcolumn(i, "ZUAMS"));
				zurate = ds_list.GetColumn(ds_list.currow, "ZURATE");	
				zurate2 = ( ParseFloat( zurate ) / 100 + 1 );
				zuam = ROUND(ParseFloat( zuams ) * ParseFloat( zurate2 ), 2);	//단가 계산	- 간접비율 포함					
				ds_detail.SetColumn(i, "ZUAM", zuam);
			}			

			if(F_PART == "part") {
				ds_detail.SetColumn(i, "WAERK", "KRW");
			} else {
				ds_detail.SetColumn(i, "WAERK", ed_tcurr.Text);
			}
		}
	}

	// str은 설계부에서 처리시만 필요(java에서)
	var str = " fa_part=" + quote(F_PART) ;		// 설계부 , 해외영업 및 상해 구분값

	nsfRequest( "save"
			   ,"ses0171/save"
			   ,"ds_detail=ds_detail ds_list=ds_list"
			   ,"ds_detail=ds_detail ds_list=ds_list ds_error=ds_error"
			   , str
			   , "fn_afterSave"
			  );

	F_QUERY_FLAG = "S";
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(svcId, errCode, errMsg) {

	fn_destroyWait();

	if (ds_error.rowcount() > 0)	{
		gfn_showAlert(ds_error.GetColumn(0,"ERRCODE"));
		return;
	}

	var addt01 = ToString(ed_sum.Text).Replace(",", "");

	// 해외본팀의 상해작의 경우 
	// 설계부에서 처리된 경우 구분하여 처리
	if (F_PART == "part")	{	// 설계부처리
		parent.fn_query_detail(addt01); // 원가산출
	}else	{	// 해외본팀 상해
		// 견적상세의 MO에 대한 단가를 확인 하여 모두 입력된 경우  
		// 원가산출완료(32)로 ZPRGFLG 처리	
		var arg = [ds_list.GetColumn(0, "ZUAM") ,		//단가
				   ds_list.GetColumn(0, "ZUAMS"),		//단가(상해)
				   ds_list.GetColumn(0, "ZCOST"),		//원가
				   ds_list.GetColumn(0, "ZEAM") ,		//견적가
				   ds_list.GetColumn(0, "SHANG"),		//시행율
				   ds_list.GetColumn(0, "ZURATE")		//상해간접비율
				   ];

		if (fn_parent_mo_cost_check())	{
			parent.fn_query_detail("32", arg); 		// 견적진행상태 원가산출완료(32) 처리
		}else	{
			parent.fn_query_detail("", arg);		// 견적진행상태 처리 불필요
		}
	}
	// CI00003 저장을 완료했습니다.
	gfn_showAlert("CI00003");

	fn_query();
}

/********************************************************************************
* 기      능   :  해외본팀 상해작의 경우 Mo에 단가 입력 여부 확인
********************************************************************************/
function fn_parent_mo_cost_check()
{

	var chkCnt = 0;
	var zcost;
	parent.ds_detail.FireEvent  = false;
	parent.ds_detail.Filter("    QTNUM != " + quote(ds_list.GetColumn(ds_list.currow, "QTNUM")) +
					        " && QTSER != " + quote(ds_list.GetColumn(ds_list.currow, "QTSER")) +
					        " && QTSEQ != " + quote(ds_list.GetColumn(ds_list.currow, "QTSEQ")) );
	for(var i = 0; i < parent.ds_detail.getRowCount(); i++)	{
		zcost = Round(ds_detail.GetColumn(i, "ZCOST"), 0);
		if (zcost == 0) 
			chkCnt++;
	}
	parent.ds_detail.UnFilter();
	parent.ds_detail.FireEvent  = true;

	if (chkCnt == 0) return true;
	else             return false;
}

