/********************************************************************************
* 기      능   : ds_detail 정보 변경 처리
********************************************************************************/
function ds_detail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {

	if (varNewValue != varOldValue) { 	     // 값이 변경되었을 경우 변경구분 컬럼 자동 SET 
		//obj.SetColumn(nRow, "CHK" , "1");	// 선택여부 
		gfn_changeFlag( obj, strColumnID, nRow, "FLAG"); // FLAG 변경 : U
	}

	if (ds_detail.rowcount() > 0 && ( strColumnID == "ZUAM" || strColumnID == "ZUAMS") ) { // 단가(수기원가)		
		fn_costChange();
	}
}

/********************************************************************************
* 기      능   : 단가 변경 시 처리
********************************************************************************/
function fn_costChange()
{
	var zuam = 0;	
	var zuams = 0;
	var zurate = 0;	
	var zurate2 = 0;
	var flag = "";

	// 부서정보 취득
	var strVkbur = ds_list.GetColumn(ds_list.currow, "VKBUR");

	for(var i = 0; i < ds_detail.rowcount(); i++ ) {

		flag = ds_detail.GetColumn(i, "FLAG");

		if (flag != "D") {
			zuam  = ParseFloat(zuam) + ParseFloat(ds_detail.Getcolumn(i, "ZUAM"));
			zuams = ParseFloat(zuams) + ParseFloat(ds_detail.Getcolumn(i, "ZUAMS"));
		}	
	}


	if (zuam >= 0 || zuams >= 0) {
		if (F_PART == "part")	{
			ed_sum.Text = zuam;
		} else {
			ed_sum.Text = zuams;
		}

		var znumber = ds_list.GetColumn(ds_list.currow, "ZNUMBER");
		var zeam    = 0;
		var zcost   = 0;

		if (strVkbur == "B500" || strVkbur == "B600" || strVkbur == "B700" || strVkbur == "B900" || strVkbur == "BA00"  || strVkbur == "BB00" ) {	// 해외의 경우

			var zurate = ds_list.GetColumn(ds_list.currow, "ZURATE");	
			var zurate2 = ( ParseFloat( zurate ) / 100 + 1 );

			zuam = ParseFloat( zuams ) * ParseFloat( zurate2 );	//단가 계산	- 간접비율 포함		
			zcost   = ROUND(ParseFloat(zuam)  * ParseInt(znumber), 2);		// 금액(단가 * 수량)	
			zeam = ParseFloat(zcost) * 1.1111;					// 해외의 경우 시행율 90%  상해의 경우만 처리

		}else	{
			zcost   = ROUND(ParseFloat(zuam)  * ParseInt(znumber), 2);		// 금액(단가 * 수량)	
			zeam = ParseFloat(zcost);
		}

		// 설계부에서 처리하는 경우 'part' 입력되고 있음 , 부분원가관리ses0350에서 호출
		// 해외본팀의 상해작의 경우는 견적가를 견적등록 상세에서 호출하여 처리
		if (F_PART != "part") {					
			ds_list.SetColumn(ds_list.currow, "ZUAM",    zuam); 	// 단가(display)
			ds_list.SetColumn(ds_list.currow, "ZUAMS",   zuams); 	// 상해단가
			ds_list.SetColumn(ds_list.currow, "ZCOST",  zcost); 	// 원가
			ds_list.SetColumn(ds_list.currow, "ZEAM",   Round(zeam, 0)); // 견적가
			fn_shang(ds_list.currow, zeam, zcost, 0.0); // 시행율  (90%)
		}		
	} 
	else {
		ed_sum.Text = 0;
	}
}

/********************************************************************************
* 기      능   : 해외본팀 상해작 시행율 계산
********************************************************************************/
function fn_shang(nRow, zeam, zcost, shang) { // 시행율
	if (ParseInt(zeam) == 0) {
		shang = 0;
	} else {
		shang = ParseFloat(zcost) * 100 / ParseFloat(zeam);

		if (shang >= 999) shang = 999;
		else              shang = Round(shang, 1);
	}

	ds_list.OnColumnChanged = "";
	ds_list.SetColumn(nRow, "SHANG", shang);
	ds_list.SetColumn(nRow, "FLAG", "U"   );
	ds_list.OnColumnChanged = "ds_list_OnColumnChanged";
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (varNewValue != varOldValue) { 	// 값이 변경되었을 경우 변경구분 컬럼 자동 SET 
		gfn_changeFlag( obj, strColumnID, nRow, "FLAG"); // FLAG 변경 : U
	}
}

function ds_detail_OnRowPosChanged(obj,nOldRow,nRow)
{
	if (obj.GetColumn(nOldRow, "FLAG") <> "U")
		return;  // 변경된 행만 체크

	//if (ChgYn == "N") 
	//	gfn_SetDatasetChgFlag(obj, nOldRow); // 변경된 자료가 없으면 선택여부,변경구분 초기화
}

function ds_list_OnRowPosChanged(obj,nOldRow,nRow) {
	var cnt = 0;

	for(var i=0; i < ds_detail.rowcount(); i++ ) { 
		if (ds_detail.getcolumn(i, "FLAG") != "") {
			cnt++;
		}
	}

	if (cnt != 0) {
//		if (F_QUERY_FLAG == "Q") fn_detail_save();
	}
	fn_Query_detail();
}

