/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {

	var arr = ['납기일자', '12'];
	if(gfn_getDayCnt(ds_days, cal_edatufr.Value, cal_edatuto.Value) > 365) {
		gfn_showAlert("CW10087", arr);
		cal_edatufr.SetFocus();
		return false;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}
	//ds_header.fireEvent = false;

	ds_cond.ClearData();
	ds_cond.InsertRow(0);
	ds_cond.SetColumn(0, "EDATUFR"  , cal_edatufr.Value     	  );
	ds_cond.SetColumn(0, "EDATUTO"  , cal_edatuto.Value     	  );
	ds_cond.SetColumn(0, "VBELN"    , ed_pjt.Text 				  );
	ds_cond.SetColumn(0, "VKBUR"    , div_vbvg.cb_frvb.Value	  );
	ds_cond.SetColumn(0, "VKGRP"    , div_vbvg.cb_frvg.Value 	  );
	ds_cond.SetColumn(0, "ZKUNNR"   , div_sman_fr.ed_sman_cd.Text );
	ds_cond.SetColumn(0, "REGION"   , ed_region.Text         	  );

	nsfRequest("header", "ses0243/header", "ds_cond=ds_cond ds_header=ds_header", "ds_header=ds_header ds_error=ds_error", "", "fn_afterQuery");
    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail() {	
	ds_cond.ClearData();
	ds_cond.InsertRow(0);
	ds_cond.SetColumn(0, "MANDT", ds_header.GetColumn(ds_header.rowpos, "MANDT"));
	ds_cond.SetColumn(0, "VBELN", ds_header.GetColumn(ds_header.rowpos, "VBELN"));
	ds_cond.SetColumn(0, "ZSEQ", ds_header.GetColumn(ds_header.rowpos, "ZSEQ"));
	ds_cond.SetColumn(0, "POSNR", ds_header.GetColumn(ds_header.rowpos, "POSNR"));

	nsfRequest("detail", "ses0243/detail", "ds_cond=ds_cond ds_detail=ds_detail", "ds_detail=ds_detail ds_error=ds_error", "", "fn_afterQuery");
}


/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save()
{
	// 그리드 저장 대상 로우
	if(ds_header.GetUpdate() == false && ds_detail.GetUpdate() == false) 
	{ 
		gfn_showAlert("CW00006", lbTxt);
		return false; 
	} 

	if(!gfn_showConfirm("CI00008")){		//저장 하시겠습니까?
	   return;
	}		

	tit_clearActionInfo();

	// 서비스 호출 
	ds_error.ClearData(); 

	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	nsfRequest("update"																// srvId
				, "ses0243/save"													// url
				, "ds_header=ds_header:U ds_detail=ds_detail:U"						// inDsList
				, "ds_error=ds_error"												// outDsList
				, ""																// strParam
				, "fn_aftersave"													// CallBackFunc
				);

}


/********************************************************************************
* 기      능   : 전송
********************************************************************************/
function fn_trans() {

	ds_header2.ClearData();
	for ( var i = 0 ; i < ds_header.GetRowCount() ; i++ ) {
		if ( ds_header.GetColumn(i, "FLAG") == 1 ) {
			if ( ds_header.GetColumn(i, "SEDAT") != "00000000" ) {
				gfn_showAlert("CW00027");	//선택한 자료는 이미 전송을 완료한 자료입니다.
				return;
			}

			var nRow = ds_header2.AddRow();
			ds_header2.CopyRow(nRow, "ds_header", i);
		}
	}

	// 그리드 전송 대상 로우
	if(ds_header2.RowCount() < 1){
		gfn_showAlert("CW10021");		//선택된 데이터가 없습니다. \n확인하시기 바랍니다.
		return;
	}

	if(!gfn_showConfirm("CI00010")){		//전송하시겠습니까
	   return;
	}		

	ds_email.ClearData();

	for (var i=0; i<ds_header2.rowcount; i++) {
		var sendto = ds_header2.GetColumn(i, "MAILADDR");	//MJ.SHIN@hdel.co.kr
		var subject = "MN_" + ds_header.GetColumn(ds_header2.currow, "VBELN") + ". " + ds_header.GetColumn(ds_header2.currow, "BSTKD");
		var content = "Dear Sirs \n Please find Manufacturing Notice of " + subject + " as attached and \n Start manufacturing immediately \n\n Best Regards,\n Hyundai Global marketing & Sales in Seoul";

		var eRow = ds_email.AppendRow();
		ds_email.SetColumn(eRow, "FROM"		, G_EMAIL);
		ds_email.SetColumn(eRow, "TO"		, sendto);
		ds_email.SetColumn(eRow, "TITLE"	, subject);
		ds_email.SetColumn(eRow, "CONTENT"	, content);
	}

	tit_clearActionInfo();

	// 서비스 호출 
	ds_error.ClearData(); 

	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	nsfRequest("sendmail"																	// srvId
				, "ses0243/sendmail"														// url
				, "ds_list=ds_header2 ds_email=ds_email"									// inDsList
				, "ds_error=ds_error"												    	// outDsList
				, ""																		// strParam
				, "fn_aftersend"															// CallBackFunc
				);
}

/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print() {
	var nHeaderCheckedRow = -1;

	ds_header2.ClearData();
	for ( var i = 0 ; i < ds_header.GetRowCount() ; i++ ) {
		if ( ds_header.GetColumn(i, "FLAG") == 1 ) {
			var nRow = ds_header2.AddRow();
			ds_header2.CopyRow(nRow, "ds_header", i);
			nHeaderCheckedRow = i;
		}
	}
	if ( ds_header2.GetRowCount() > 1 ) {
		gfn_showAlert("CW00222");		//하나의 자료만 선택하여 주십시오.
		return;
	} else if ( ds_header2.GetRowCount() == 0 ) {
		gfn_showAlert("CW10021");		//선택된 데이터가 없습니다. \n확인하시기 바랍니다.
		return;
	}

	// CHECK한 행이 현재 선택한 행일 경우
	if ( nHeaderCheckedRow == ds_header.row ) {
		var sumKwmeng = 0;
		var sumNetwr = 0;
		var vrkme;

		sumKwmeng = ParseInt(ds_detail.TotalSum("KWMENG"));	//수량합계
		sumNetwr = ParseInt(ds_detail.TotalSum("PONETWR"));	//상해금액합계
		vrkme = ds_detail.GetColumn(0, "VRKME");	//판매단위

		var arg = "sumKwmeng=" + sumKwmeng
				+ " sumNetwr=" + sumNetwr
				+ " vrkme=" + vrkme;
		Dialog("SES::Ses0246.xml", arg, -1, -1, "Resize=true");	// UbiReport Call
	} 
	// CHECK한 행이 현재 선택한 행이 아닐 경우
	else {
		F_PRINT_FLAG = 1;
		ds_header.row = nHeaderCheckedRow;
		fn_detail(ds_header.row);
	}
}

