********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {	
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	if (svcId == "header") 
	{
		fn_detail();
	}
	else
	{
		fn_destroyWait();
		if ( F_PRINT_FLAG == 1 ) {
			F_PRINT_FLAG = 0;
			fn_print();
		}
	}

	if(ds_error.rowcount() > 0) {

		gfn_showMsg("CE00001");
		gfn_openWSErrorWin(grd_header, ds_header, ds_error, "FLAG");	// error 정보 표시 

	} else {
		if(errCode != 0) gfn_showMsg("CE00001");
		else             gfn_showMsg("CI00002", ds_header.RowCount() + "");
	}
} 

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_aftersave(errCode, errMsg) {
	fn_destroyWait();

	if ( ds_error.rowcount > 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
		fn_query();
	}	
}

/********************************************************************************
* 기      능   : 전송 Callback  
********************************************************************************/
function fn_aftersend(errCode, errMsg) {
	fn_destroyWait();

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}

		fn_query();
}


