/********************************************************************************
* 기      능   : 프로젝트 팝업
********************************************************************************/
function ufn_openPjt() {
	var arg = "pjt="+quote(ed_pjt.Text);
	var arr = gfn_openPopup("CS9601002A",true,arg);

	if (arr != null && arr != 0) {
		ed_pjt.Text = arr[0];
		ed_pjtnm.Text = arr[2];
	}
}

/********************************************************************************
* 기      능   : 국가 팝업
********************************************************************************/
function unf_opneContry_OnClick(obj)
{
	var param = ed_region.Text;
	var arg  = "popFlag="+"nation"; // pop구분값 ( nation:국가 / area:운송지역 )
		arg += " pCdNm="+quote(param); // 넘길 값

	var arr = gfn_openPopupUrl("COM::Com0060.xml", true, arg);

	if (arr != null && arr != 0)
	{
		ed_region.Text = arr[0];
		ed_regionnm.Text = arr[1];
		ed_region.SetFocus();
	}
}
/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_Header_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {	
	if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
		fn_detail();
	}
}

/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_header_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID!="FLAG"){
		ds_header.SetColumn(nRow, "FLAG", 1);
	}
}
/********************************************************************************
* 기      능   : M/N생성 팝업
********************************************************************************/
function btnMn_OnClick(obj)
{

	var arg = "pGubun = 'U'";  
	arg = arg +  " pVbeln   = "  + quote(ds_header.getcolumn(ds_header.row, "ZZPJT_ID"  ));

    var rtn = gfn_openPopUpUrlHdel("Ses::Ses0245.xml", true, arg );

    if (rtn == "SAVE") fn_query();
}

/********************************************************************************
* 기      능   : Header Dataset Row Pos Changed Event
********************************************************************************/
function ds_header_OnRowPosChanged(obj,nOldRow,nRow)
{
	// 메일전송일자가 지정되어 있으면 비고 수정불가
	if( ds_header.GetColumn(nRow, "SEDAT") != "00000000")
	{		
		grd_header_remark.Editable = false;
	}
	else
	{
		grd_header_remark.Editable = true;
	}

	fn_detail();
}

/********************************************************************************
* 기      능   : 비고 변경시
********************************************************************************/
function ds_header_remark_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_header.CopyRow(ds_header.row, obj, 0);
}
/********************************************************************************
* 기      능   : 비고 데이터셋 추가
********************************************************************************/
function grd_header_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	ds_header_remark.fireEvent = false;
	ds_header_remark.ClearData();
	ds_header_remark.AddRow();
	ds_header_remark.CopyRow(0, ds_header, nRow);
	ds_header_remark.fireEvent = true;
}

/********************************************************************************
* 기      능   : Detail DataSet Column Changed Event
********************************************************************************/
function ds_detail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch(strColumnID) {
		case "PONETWR" :
			ds_header.SetColumn(ds_header.rowpos, "PONETWR", ds_detail.TotalSum("PONETWR"));
			break;
	}
}
/******************************************************
// Code Value 변경시 명칭 clear처리
******************************************************/
function Common_Edit_OnCharChanged(obj,strPreText,strPostText)
{
	switch(obj.id) {
		case "ed_pjt" :
			ed_pjtnm.Text = "";
			break;
		case "ed_region" :
			ed_regionnm.Text = "";
			break;
	}
}
